insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 1, 'Adán, Set, Enós, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 2, 'Cainán, Mahalaleel, Jared, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 3, 'Enoc, Matusalén, Lamec, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 4, 'Noé, Sem, Cam y Jafet.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 5, 'Los hijos de Jafet: Gomer, Magog, Madai, Javán, Tubal, Mesec y Tiras. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 6, 'Los hijos de Gomer: Askenaz, Rifat y Togarma.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 7, 'Los hijos de Javán: Elisa, Tarsis, Quitim y Dodanim.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 8, 'Los hijos de Cam: Cus, Mizraim, Fut y Canaán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 9, 'Los hijos de Cus: Seba, Havila, Sabta, Raama y Sabteca. Y los hijos de Raama: Seba y Dedán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 10, 'Cus engendró a Nimrod; este llegó a ser poderoso en la tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 11, 'Mizraim engendró a Ludim, Anamim, Lehabim, Naftuhim, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 12, 'Patrusim y Casluhim; de estos salieron los filisteos y los caftoreos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 13, 'Canaán engendró a Sidón su primogénito, y a Het, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 14, 'al jebuseo, al amorreo, al gergeseo, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 15, 'al heveo, al araceo, al sineo, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 16, 'al arvadeo, al zemareo y al hamateo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 17, 'Los hijos de Sem: Elam, Asur, Arfaxad, Lud, Aram, Uz, Hul, Geter y Mesec.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 18, 'Arfaxad engendró a Sela, y Sela engendró a Heber.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 19, 'Y a Heber nacieron dos hijos; el nombre del uno fue Peleg, por cuanto en sus días fue dividida la tierra; y el nombre de su hermano fue Joctán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 20, 'Joctán engendró a Almodad, Selef, Hazar-mavet y Jera.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 21, 'A Adoram también, a Uzal, Dicla, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 22, 'Ebal, Abimael, Seba, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 23, 'Ofir, Havila y Jobab; todos hijos de Joctán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 24, 'Sem, Arfaxad, Sela, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 25, 'Heber, Peleg, Reu, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 26, 'Serug, Nacor, Taré, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 27, ' Abram, el cual es Abraham.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 28, 'Los hijos de Abraham: Isaac e Ismael.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 29, 'Y estas son sus descendencias: el primogénito de Ismael, Nebaiot; después Cedar, Adbeel, Mibsam, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 30, 'Misma, Duma, Massa, Hadad, Tema, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 31, 'Jetur, Nafis y Cedema; estos son los hijos de Ismael.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 32, 'Y Cetura, concubina de Abraham, dio a luz a Zimram, Jocsán, Medán, Madián, Isbac y Súa. Los hijos de Jocsán: Seba y Dedán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 33, 'Los hijos de Madián: Efa, Efer, Hanoc, Abida y Elda; todos estos fueron hijos de Cetura.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 34, 'Abraham engendró a Isaac, y los hijos de Isaac fueron Esaú e Israel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 35, 'Los hijos de Esaú: Elifaz, Reuel, Jeús, Jaalam y Coré.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 36, 'Los hijos de Elifaz: Temán, Omar, Zefo, Gatam, Cenaz, Timna y Amalec.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 37, 'Los hijos de Reuel: Nahat, Zera, Sama y Miza.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 38, 'Los hijos de Seir: Lotán, Sobal, Zibeón, Aná, Disón, Ezer y Disán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 39, 'Los hijos de Lotán: Hori y Homam; y Timna fue hermana de Lotán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 40, 'Los hijos de Sobal: Alván, Manahat, Ebal, Sefo y Onam. Los hijos de Zibeón: Aja y Aná.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 41, 'Disón fue hijo de Aná; y los hijos de Disón: Amram, Esbán, Itrán y Querán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 42, 'Los hijos de Ezer: Bilhán, Zaaván y Jaacán. Los hijos de Disán: Uz y Arán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 43, 'Y estos son los reyes que reinaron en la tierra de Edom, antes que reinase rey sobre los hijos de Israel: Bela hijo de Beor; y el nombre de su ciudad fue Dinaba.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 44, 'Muerto Bela, reinó en su lugar Jobab hijo de Zera, de Bosra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 45, 'Y muerto Jobab, reinó en su lugar Husam, de la tierra de los temanitas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 46, 'Muerto Husam, reinó en su lugar Hadad hijo de Bedad, el que derrotó a Madián en el campo de Moab; y el nombre de su ciudad fue Avit.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 47, 'Muerto Hadad, reinó en su lugar Samla de Masreca.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 48, 'Muerto también Samla, reinó en su lugar Saúl de Rehobot, que está junto al Éufrates.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 49, 'Y muerto Saúl, reinó en su lugar Baal-hanán hijo de Acbor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 50, 'Muerto Baal-hanán, reinó en su lugar Hadad, el nombre de cuya ciudad fue Pai; y el nombre de su mujer, Mehetabel hija de Matred, hija de Mezaab.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 51, 'Muerto Hadad, sucedieron en Edom los jefes Timna, Alva, Jetet, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 52, 'Aholibama, Ela, Pinón, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 53, 'Cenaz, Temán, Mibzar, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 1, 54, 'Magdiel e Iram. Estos fueron los jefes de Edom.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 1, 'Estos son los hijos de Israel: Rubén, Simeón, Leví, Judá, Isacar, Zabulón, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 2, 'Dan, José, Benjamín, Neftalí, Gad y Aser.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 3, 'Los hijos de Judá: Er, Onán y Sela. Estos tres le nacieron de la hija de Súa, cananea. Y Er, primogénito de Judá, fue malo delante de Jehová, quien lo mató.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 4, 'Y Tamar su nuera dio a luz a Fares y a Zera. Todos los hijos de Judá fueron cinco.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 5, 'Los hijos de Fares: Hezrón y Hamul.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 6, 'Y los hijos de Zera: Zimri, Etán, Hemán, Calcol y Dara; por todos cinco.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 7, 'Hijo de Carmi fue Acán, el que perturbó a Israel, porque prevaricó en el anatema.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 8, 'Azarías fue hijo de Etán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 9, 'Los hijos que nacieron a Hezrón: Jerameel, Ram y Quelubai.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 10, 'Ram engendró a Aminadab, y Aminadab engendró a Naasón, príncipe de los hijos de Judá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 11, 'Naasón engendró a Salmón, y Salmón engendró a Booz.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 12, 'Booz engendró a Obed, y Obed engendró a Isaí, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 13, 'e Isaí engendró a Eliab su primogénito, el segundo Abinadab, Simea el tercero, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 14, 'el cuarto Natanael, el quinto Radai, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 15, 'el sexto Ozem, el séptimo David, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 16, 'de los cuales Sarvia y Abigail fueron hermanas. Los hijos de Sarvia fueron tres: Abisai, Joab y Asael.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 17, 'Abigail dio a luz a Amasa, cuyo padre fue Jeter ismaelita, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 18, 'Caleb hijo de Hezrón engendró a Jeriot de su mujer Azuba. Y los hijos de ella fueron Jeser, Sobab y Ardón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 19, 'Muerta Azuba, tomó Caleb por mujer a Efrata, la cual dio a luz a Hur.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 20, 'Y Hur engendró a Uri, y Uri engendró a Bezaleel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 21, 'Después entró Hezrón a la hija de Maquir padre de Galaad, la cual tomó siendo él de sesenta años, y ella dio a luz a Segub.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 22, 'Y Segub engendró a Jair, el cual tuvo veintitrés ciudades en la tierra de Galaad.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 23, 'Pero Gesur y Aram tomaron de ellos las ciudades de Jair, con Kenat y sus aldeas, sesenta lugares. Todos estos fueron de los hijos de Maquir padre de Galaad.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 24, 'Muerto Hezrón en Caleb de Efrata, Abías mujer de Hezrón dio a luz a Asur padre de Tecoa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 25, 'Los hijos de Jerameel primogénito de Hezrón fueron Ram su primogénito, Buna, Orén, Ozem y Ahías.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 26, 'Y tuvo Jerameel otra mujer llamada Atara, que fue madre de Onam.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 27, 'Los hijos de Ram primogénito de Jerameel fueron Maaz, Jamín y Equer.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 28, 'Y los hijos de Onam fueron Samai y Jada. Los hijos de Samai: Nadab y Abisur.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 29, 'Y el nombre de la mujer de Abisur fue Abihail, la cual dio a luz a Ahbán y a Molid.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 30, 'Los hijos de Nadab: Seled y Apaim. Y Seled murió sin hijos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 31, 'Isi fue hijo de Apaim, y Sesán hijo de Isi, e hijo de Sesán, Ahlai.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 32, 'Los hijos de Jada hermano de Samai: Jeter y Jonatán. Y murió Jeter sin hijos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 33, 'Los hijos de Jonatán: Pelet y Zaza. Estos fueron los hijos de Jerameel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 34, 'Y Sesán no tuvo hijos, sino hijas; pero tenía Sesán un siervo egipcio llamado Jarha.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 35, 'A este Sesán dio su hija por mujer, y ella dio a luz a Atai.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 36, 'Atai engendró a Natán, y Natán engendró a Zabad;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 37, 'Zabad engendró a Eflal, Eflal engendró a Obed;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 38, 'Obed engendró a Jehú, Jehú engendró a Azarías;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 39, 'Azarías engendró a Heles, Heles engendró a Elasa;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 40, 'Elasa engendró a Sismai, Sismai engendró a Salum;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 41, 'Salum engendró a Jecamías, y Jecamías engendró a Elisama.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 42, 'Los hijos de Caleb hermano de Jerameel fueron: Mesa su primogénito, que fue el padre de Zif; y los hijos de Maresa padre de Hebrón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 43, 'Y los hijos de Hebrón: Coré, Tapúa, Requem y Sema.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 44, 'Sema engendró a Raham padre de Jorcoam, y Requem engendró a Samai.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 45, 'Maón fue hijo de Samai, y Maón padre de Bet-sur.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 46, 'Y Efa concubina de Caleb dio a luz a Harán, a Mosa y a Gazez. Y Harán engendró a Gazez.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 47, 'Los hijos de Jahdai: Regem, Jotam, Gesam, Pelet, Efa y Saaf.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 48, 'Maaca concubina de Caleb dio a luz a Seber y a Tirhana.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 49, 'También dio a luz a Saaf padre de Madmana, y a Seva padre de Macbena y padre de Gibea. Y Acsa fue hija de Caleb.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 50, 'Estos fueron los hijos de Caleb. Los hijos de Hur primogénito de Efrata: Sobal padre de Quiriat-jearim, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 51, 'Salma padre de Belén, y Haref padre de Bet-gader.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 52, 'Y los hijos de Sobal padre de Quiriat-jearim fueron Haroe, la mitad de los manahetitas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 53, 'Y las familias de Quiriat-jearim fueron los itritas, los futitas, los sumatitas y los misraítas, de los cuales salieron los zoratitas y los estaolitas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 54, 'Los hijos de Salma: Belén, y los netofatitas, Atrot-bet-joab, y la mitad de los manahetitas, los zoraítas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 2, 55, 'Y las familias de los escribas que moraban en Jabes fueron los tirateos, los simeateos y los sucateos, los cuales son los ceneos que vinieron de Hamat padre de la casa de Recab.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 1, 'Estos son los hijos de David que le nacieron en Hebrón: Amnón el primogénito, de Ahinoam jezreelita; el segundo, Daniel, de Abigail la de Carmel;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 2, 'el tercero, Absalón hijo de Maaca, hija de Talmai rey de Gesur; el cuarto, Adonías hijo de Haguit;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 3, 'el quinto, Sefatías, de Abital; el sexto, Itream, de Egla su mujer.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 4, 'Estos seis le nacieron en Hebrón, donde reinó siete años y seis meses; y en Jerusalén reinó treinta y tres años.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 5, 'Estos cuatro le nacieron en Jerusalén: Simea, Sobab, Natán, y Salomón hijo de Bet-súa hija de Amiel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 6, 'Y otros nueve: Ibhar, Elisama, Elifelet, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 7, 'Noga, Nefeg, Jafía, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 8, 'Elisama, Eliada y Elifelet.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 9, 'Todos estos fueron los hijos de David, sin los hijos de las concubinas. Y Tamar fue hermana de ellos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 10, 'Hijo de Salomón fue Roboam, cuyo hijo fue Abías, del cual fue hijo Asa, cuyo hijo fue Josafat, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 11, 'de quien fue hijo Joram, cuyo hijo fue Ocozías, hijo del cual fue Joás, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 12, 'del cual fue hijo Amasías, cuyo hijo fue Azarías, e hijo de este, Jotam. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 13, 'Hijo de este fue Acaz, del que fue hijo Ezequías, cuyo hijo fue Manasés, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 14, 'el cual fue hijo Amón, cuyo hijo fue Josías.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 15, 'Y los hijos de Josías: Johanán su primogénito, el segundo Joacim, el tercero Sedequías, el cuarto Salum.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 16, 'Los hijos de Joacim: Jeconías su hijo, hijo del cual fue Sedequías.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 17, 'Y los hijos de Jeconías: Asir, Salatiel, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 18, 'Malquiram, Pedaías, Senazar, Jecamías, Hosama y Nedabías.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 19, 'Los hijos de Pedaías: Zorobabel y Simei. Y los hijos de Zorobabel: Mesulam, Hananías, y Selomit su hermana;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 20, 'y Hasuba, Ohel, Berequías, Hasadías y Jusab-hesed; cinco por todos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 21, 'Los hijos de Hananías: Pelatías y Jesaías; su hijo, Refaías; su hijo, Arnán; su hijo, Abdías; su hijo, Secanías.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 22, 'Hijo de Secanías fue Semaías; y los hijos de Semaías: Hatús, Igal, Barías, Nearías y Safat, seis.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 23, 'Los hijos de Nearías fueron estos tres: Elioenai, Ezequías y Azricam.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 3, 24, 'Los hijos de Elioenai fueron estos siete: Hodavías, Eliasib, Pelaías, Acub, Johanán, Dalaías y Anani.');
