insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 1, 'De allí partió Abraham a la tierra del Neguev, y acampó entre Cades y Shur, y habitó como forastero en Gerar. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 2, 'Y dijo Abraham de Sara su mujer: Es mi hermana. Y Abimelec rey de Gerar envió y tomó a Sara. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 3, 'Pero Dios vino a Abimelec en sueños de noche, y le dijo: He aquí, muerto eres, a causa de la mujer que has tomado, la cual es casada con marido. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 4, 'Mas Abimelec no se había llegado a ella, y dijo: Señor, ¿matarás también al inocente?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 5, '¿No me dijo él: Mi hermana es; y ella también dijo: Es mi hermano? con sencillez de mi corazón y con limpieza de mis manos he hecho esto. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 6, 'Y le dijo Dios en sueños: Yo también sé que con integridad de tu corazón has hecho esto; y yo también te detuve de pecar contra mí, y así no te permití que la tocases. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20,  7, 'Ahora, pues, devuelve la mujer a su marido; porque es profeta, y orará por ti, y vivirás. Y si no la devolvieres, sabe que de cierto morirás tú, y todos los tuyos. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 8, ' Entonces Abimelec se levantó de mañana y llamó a todos sus siervos, y dijo todas estas palabras en los oídos de ellos; y temieron los hombres en gran manera. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 9, 'Después llamó Abimelec a Abraham, y le dijo: ¿Qué nos has hecho? ¿En qué pequé yo contra ti, que has atraído sobre mí y sobre mi reino tan grande pecado? Lo que no debiste hacer has hecho conmigo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 10, 'Dijo también Abimelec a Abraham: ¿Qué pensabas, para que hicieses esto? ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 11, 'Y Abraham respondió: Porque dije para mí: Ciertamente no hay temor de Dios en este lugar, y me matarán por causa de mi mujer.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 12, ' Y a la verdad también es mi hermana, hija de mi padre, mas no hija de mi madre, y la tomé por mujer. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 13, 'Y cuando Dios me hizo salir errante de la casa de mi padre, yo le dije: Esta es la merced que tú harás conmigo, que en todos los lugares adonde lleguemos, digas de mí: Mi hermano es.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 14, 'Entonces Abimelec tomó ovejas y vacas, y siervos y siervas, y se los dio a Abraham, y le devolvió a Sara su mujer. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 15, 'Y dijo Abimelec: He aquí mi tierra está delante de ti; habita donde bien te parezca. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 16, 'Y a Sara dijo: He aquí he dado mil monedas de plata a tu hermano; mira que él te es como un velo para los ojos de todos los que están contigo, y para con todos; así fue vindicada. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 17, 'Entonces Abraham oró a Dios; y Dios sanó a Abimelec y a su mujer, y a sus siervas, y tuvieron hijos. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 20, 18, ' Porque Jehová había cerrado completamente toda matriz de la casa de Abimelec, a causa de Sara mujer de Abraham. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 1, 'Visitó Jehová a Sara, como había dicho, e hizo Jehová con Sara como había hablado. ');																					
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 2, 'Y Sara concibió y dio a Abraham un hijo en su vejez, en el tiempo que Dios le había dicho. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 3, 'Y llamó Abraham el nombre de su hijo que le nació, que le dio a luz Sara, Isaac. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 4, ' Y circuncidó Abraham a su hijo Isaac de ocho días, como Dios le había mandado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 5, 'Y era Abraham de cien años cuando nació Isaac su hijo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 6, 'Entonces dijo Sara: Dios me ha hecho reir, y cualquiera que lo oyere, se reirá conmigo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 7,'Y añadió: ¿Quién dijera a Abraham que Sara habría de dar de mamar a hijos? Pues le he dado un hijo en su vejez.Agar e Ismael son echados de la casa de Abraham.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 8, ' Y creció el niño, y fue destetado; e hizo Abraham gran banquete el día que fue destetado Isaac.');		
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 9, 'Y vio Sara que el hijo de Agar la egipcia, el cual ésta le había dado a luz a Abraham, se burlaba de su hijo Isaac.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 10, 'Por tanto, dijo a Abraham: Echa a esta sierva y a su hijo, porque el hijo de esta sierva no ha de heredar con Isaac mi hijo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 11, 'Este dicho pareció grave en gran manera a Abraham a causa de su hijo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 12, 'Entonces dijo Dios a Abraham: No te parezca grave a causa del muchacho y de tu sierva; en todo lo que te dijere Sara, oye su voz, porque en Isaac te será llamada descendencia.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 13, 'Y también del hijo de la sierva haré una nación, porque es tu descendiente. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 14, 'Entonces Abraham se levantó muy de mañana, y tomó pan, y un odre de agua, y lo dio a Agar, poniéndolo sobre su hombro, y le entregó el muchacho, y la despidió. Y ella salió y anduvo errante por el desierto de Beerseba.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 15, 'Y le faltó el agua del odre, y echó al muchacho debajo de un arbusto, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 16, 'y se fue y se sentó enfrente, a distancia de un tiro de arco; porque decía: No veré cuando el muchacho muera. Y cuando ella se sentó enfrente, el muchacho alzó su voz y lloró.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 17,	'Y oyó Dios la voz del muchacho; y el ángel de Dios llamó a Agar desde el cielo, y le dijo: ¿Qué tienes, Agar? No temas; porque Dios ha oído la voz del muchacho en donde está.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 18, 'Levántate, alza al muchacho, y sostenlo con tu mano, porque yo haré de él una gran nación. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 19, 'Entonces Dios le abrió los ojos, y vio una fuente de agua; y fue y llenó el odre de agua, y dio de beber al muchacho.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 20, 'Y Dios estaba con el muchacho; y creció, y habitó en el desierto, y fue tirador de arco. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 21, '	Y habitó en el desierto de Parán; y su madre le tomó mujer de la tierra de Egipto. Pacto entre Abraham y Abimelec');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 22, ' Aconteció en aquel mismo tiempo que habló Abimelec, y Ficol príncipe de su ejército, a Abraham, diciendo: Dios está contigo en todo cuanto haces.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 23, 'Ahora, pues, júrame aquí por Dios, que no faltarás a mí, ni a mi hijo ni a mi nieto, sino que conforme a la bondad que yo hice contigo, harás tú conmigo, y con la tierra en donde has morado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 24, ' Y respondió Abraham: Yo juraré.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 25, ' Y Abraham reconvino a Abimelec a causa de un pozo de agua, que los siervos de Abimelec le habían quitado. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 26, 'Y respondió Abimelec: No sé quién haya hecho esto, ni tampoco tú me lo hiciste saber, ni yo lo he oído hasta hoy. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 27, 'Y tomó Abraham ovejas y vacas, y dio a Abimelec; e hicieron ambos pacto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 28, 'Entonces puso Abraham siete corderas del rebaño aparte.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 29, 'Y dijo Abimelec a Abraham: ¿Qué significan esas siete corderas que has puesto aparte?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 30, 'Y él respondió: Que estas siete corderas tomarás de mi mano, para que me sirvan de testimonio de que yo cavé este pozo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 31, 'Por esto llamó a aquel lugar Beerseba; porque allí juraron ambos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 32, 'Así hicieron pacto en Beerseba; y se levantó Abimelec, y Ficol príncipe de su ejército, y volvieron a tierra de los filisteos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 33, ' Y plantó Abraham un árbol tamarisco en Beerseba, e invocó allí el nombre de Jehová Dios eterno.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 21, 34, 'Y moró Abraham en tierra de los filisteos muchos días.  Dios ordena a Abraham que sacrifique a Isaac');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 1, 'Aconteció después de estas cosas, que probó Dios a Abraham, y le dijo: Abraham. Y él respondió: Heme aquí.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 2, ' Y dijo: Toma ahora tu hijo, tu único, Isaac, a quien amas, y vete a tierra de Moriah, y ofrécelo allí en holocausto sobre uno de los montes que yo te diré.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 3, 'Y Abraham se levantó muy de mañana, y enalbardó su asno, y tomó consigo dos siervos suyos, y a Isaac su hijo; y cortó leña para el holocausto, y se levantó, y fue al lugar que Dios le dijo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 4, 'Al tercer día alzó Abraham sus ojos, y vio el lugar de lejos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 5, ' Entonces dijo Abraham a sus siervos: Esperad aquí con el asno, y yo y el muchacho iremos hasta allí y adoraremos, y volveremos a vosotros.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 6, 'Y tomó Abraham la leña del holocausto, y la puso sobre Isaac su hijo, y él tomó en su mano el fuego y el cuchillo; y fueron ambos juntos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 7, ' Entonces habló Isaac a Abraham su padre, y dijo: Padre mío. Y él respondió: Heme aquí, mi hijo. Y él dijo: He aquí el fuego y la leña; mas ¿dónde está el cordero para el holocausto?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 8, 'Y respondió Abraham: Dios se proveerá de cordero para el holocausto, hijo mío. E iban juntos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 9, 'Y cuando llegaron al lugar que Dios le había dicho, edificó allí Abraham un altar, y compuso la leña, y ató a Isaac su hijo, y lo puso en el altar sobre la leña.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 10, 'Y extendió Abraham su mano y tomó el cuchillo para degollar a su hijo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 11, 'Entonces el ángel de Jehová le dio voces desde el cielo, y dijo: Abraham, Abraham. Y él respondió: Heme aquí.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 12, 'Y dijo: No extiendas tu mano sobre el muchacho, ni le hagas nada; porque ya conozco que temes a Dios, por cuanto no me rehusaste tu hijo, tu único.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 13, 'Entonces alzó Abraham sus ojos y miró, y he aquí a sus espaldas un carnero trabado en un zarzal por sus cuernos; y fue Abraham y tomó el carnero, y lo ofreció en holocausto en lugar de su hijo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 14, 'Y llamó Abraham el nombre de aquel lugar, Jehová proveerá. Por tanto se dice hoy: En el monte de Jehová será provisto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 15, 'Y llamó el ángel de Jehová a Abraham por segunda vez desde el cielo,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 16, 'y dijo: Por mí mismo he jurado, dice Jehová, que por cuanto has hecho esto, y no me has rehusado tu hijo, tu único hijo;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 17, 'de cierto te bendeciré, y multiplicaré tu descendencia como las estrellas del cielo y como la arena que está a la orilla del mar; y tu descendencia poseerá las puertas de sus enemigos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 18, 'En tu simiente serán benditas todas las naciones de la tierra, por cuanto obedeciste a mi voz.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 19, ' Y volvió Abraham a sus siervos, y se levantaron y se fueron juntos a Beerseba; y habitó Abraham en Beerseba.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 20, 'Aconteció después de estas cosas, que fue dada noticia a Abraham, diciendo: He aquí que también Milca ha dado a luz hijos a Nacor tu hermano:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 21, 'Uz su primogénito, Buz su hermano, Kemuel padre de Aram,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 22, ' Quesed, Hazo, Pildas, Jidlaf y Betuel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 23, 'Y Betuel fue el padre de Rebeca. Estos son los ocho hijos que dio a luz Milca, de Nacor hermano de Abraham.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 22, 24, ' Y su concubina, que se llamaba Reúma, dio a luz también a Teba, a Gaham, a Tahas y a Maaca.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 1, 'Aconteció en aquel tiempo, que Judá se apartó de sus hermanos, y se fue a un varón adulamita que se llamaba Hira.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 2, 'Y vio allí Judá la hija de un hombre cananeo, el cual se llamaba Súa; y la tomó, y se llegó a ella.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 3, 'Y ella concibió, y dio a luz un hijo, y llamó su nombre Er.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 4, 'Concibió otra vez, y dio a luz un hijo, y llamó su nombre Onán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 5, 'Y volvió a concebir, y dio a luz un hijo, y llamó su nombre Sela. Y estaba en Quezib cuando lo dio a luz.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 6, 'Después Judá tomó mujer para su primogénito Er, la cual se llamaba Tamar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 7, 'Y Er, el primogénito de Judá, fue malo ante los ojos de Jehová, y le quitó Jehová la vida.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 8, 'Entonces Judá dijo a Onán: Llégate a la mujer de tu hermano, y despósate con ella, y levanta descendencia a tu hermano.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 9, 'Y sabiendo Onán que la descendencia no había de ser suya, sucedía que cuando se llegaba a la mujer de su hermano, vertía en tierra, por no dar descendencia a su hermano.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 10, 'Y desagradó en ojos de Jehová lo que hacía, y a él también le quitó la vida.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 11, 'Y Judá dijo a Tamar su nuera: Quédate viuda en casa de tu padre, hasta que crezca Sela mi hijo; porque dijo: No sea que muera él también como sus hermanos. Y se fue Tamar, y estuvo en casa de su padre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 12, 'Pasaron muchos días, y murió la hija de Súa, mujer de Judá. Después Judá se consoló, y subía a los trasquiladores de sus ovejas a Timnat, él y su amigo Hira el adulamita.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 13, 'Y fue dado aviso a Tamar, diciendo: He aquí tu suegro sube a Timnat a trasquilar sus ovejas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 14, 'Entonces se quitó ella los vestidos de su viudez, y se cubrió con un velo, y se arrebozó, y se puso a la entrada de Enaim junto al camino de Timnat; porque veía que había crecido Sela, y ella no era dada a él por mujer.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 15, 'Y la vio Judá, y la tuvo por ramera, porque ella había cubierto su rostro.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 16, 'Y se apartó del camino hacia ella, y le dijo: Déjame ahora llegarme a ti: pues no sabía que era su nuera; y ella dijo: ¿Qué me darás por llegarte a mí?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 17, 'El respondió: Yo te enviaré del ganado un cabrito de las cabras. Y ella dijo: Dame una prenda hasta que lo envíes.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 18, 'Entonces Judá dijo: ¿Qué prenda te daré? Ella respondió: Tu sello, tu cordón, y tu báculo que tienes en tu mano. Y él se los dio, y se llegó a ella, y ella concibió de él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 19, 'Luego se levantó y se fue, y se quitó el velo de sobre sí, y se vistió las ropas de su viudez.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 20, 'Y Judá envió el cabrito de las cabras por medio de su amigo el adulamita, para que éste recibiese la prenda de la mujer; pero no la halló.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 21, 'Y preguntó a los hombres de aquel lugar, diciendo: ¿Dónde está la ramera de Enaim junto al camino? Y ellos le dijeron: No ha estado aquí ramera alguna.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 22, 'Entonces él se volvió a Judá, y dijo: No la he hallado; y también los hombres del lugar dijeron: Aquí no ha estado ramera.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 23, 'Y Judá dijo: Tómeselo para sí, para que no seamos menospreciados; he aquí yo he enviado este cabrito, y tú no la hallaste.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 24, 'Sucedió que al cabo de unos tres meses fue dado aviso a Judá, diciendo: Tamar tu nuera ha fornicado, y ciertamente está encinta a causa de las fornicaciones. Y Judá dijo: Sacadla, y sea quemada.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 25, 'Pero ella, cuando la sacaban, envió a decir a su suegro: Del varón cuyas son estas cosas, estoy encinta. También dijo: Mira ahora de quién son estas cosas, el sello, el cordón y el báculo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 26, 'Entonces Judá los reconoció, y dijo: Más justa es ella que yo, por cuanto no la he dado a Sela mi hijo. Y nunca más la conoció.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 27, 'Y aconteció que al tiempo de dar a luz, he aquí había gemelos en su seno.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 28, 'Sucedió cuando daba a luz, que sacó la mano el uno, y la partera tomó y ató a su mano un hilo de grana, diciendo: Este salió primero.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 38, 29, 'Pero volviendo él a meter la mano, he aquí salió su hermano; y ella dijo: ¡Qué brecha te has abierto! Y llamó su nombre Fares. 30 Después salió su hermano, el que tenía en su mano el hilo de grana, y llamó su nombre Zara.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 1, 'Llevado, pues, José a Egipto, Potifar oficial de Faraón, capitán de la guardia, varón egipcio, lo compró de los ismaelitas que lo habían llevado allá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 2, 'Mas Jehová estaba con José, y fue varón próspero; y estaba en la casa de su amo el egipcio.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 3, 'Y vio su amo que Jehová estaba con él, y que todo lo que él hacía, Jehová lo hacía prosperar en su mano.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 4, 'Así halló José gracia en sus ojos, y le servía; y él le hizo mayordomo de su casa y entregó en su poder todo lo que tenía.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 5, 'Y aconteció que desde cuando le dio el encargo de su casa y de todo lo que tenía, Jehová bendijo la casa del egipcio a causa de José, y la bendición de Jehová estaba sobre todo lo que tenía, así en casa como en el campo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 6, 'Y dejó todo lo que tenía en mano de José, y con él no se preocupaba de cosa alguna sino del pan que comía. Y era José de hermoso semblante y bella presencia.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 7, 'Aconteció después de esto, que la mujer de su amo puso sus ojos en José, y dijo: Duerme conmigo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 8, 'Y él no quiso, y dijo a la mujer de su amo: He aquí que mi señor no se preocupa conmigo de lo que hay en casa, y ha puesto en mi mano todo lo que tiene.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 9, 'No hay otro mayor que yo en esta casa, y ninguna cosa me ha reservado sino a ti, por cuanto tú eres su mujer; ¿cómo, pues, haría yo este grande mal, y pecaría contra Dios?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 10, 'Hablando ella a José cada día, y no escuchándola él para acostarse al lado de ella, para estar con ella,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 11, 'aconteció que entró él un día en casa para hacer su oficio, y no había nadie de los de casa allí.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 12, 'Y ella lo asió por su ropa, diciendo: Duerme conmigo. Entonces él dejó su ropa en las manos de ella, y huyó y salió.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 13, 'Cuando vio ella que le había dejado su ropa en sus manos, y había huido fuera,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 14, 'llamó a los de casa, y les habló diciendo: Mirad, nos ha traído un hebreo para que hiciese burla de nosotros. Vino él a mí para dormir conmigo, y yo di grandes voces;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 15, 'y viendo que yo alzaba la voz y gritaba, dejó junto a mí su ropa, y huyó y salió.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 16, 'Y ella puso junto a sí la ropa de José, hasta que vino su señor a su casa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 17, 'Entonces le habló ella las mismas palabras, diciendo: El siervo hebreo que nos trajiste, vino a mí para deshonrarme.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 18, 'Y cuando yo alcé mi voz y grité, él dejó su ropa junto a mí y huyó fuera.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 19, 'Y sucedió que cuando oyó el amo de José las palabras que su mujer le hablaba, diciendo: Así me ha tratado tu siervo, se encendió su furor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 20, 'Y tomó su amo a José, y lo puso en la cárcel, donde estaban los presos del rey, y estuvo allí en la cárcel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 21, 'Pero Jehová estaba con José y le extendió su misericordia, y le dio gracia en los ojos del jefe de la cárcel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 22, 'Y el jefe de la cárcel entregó en mano de José el cuidado de todos los presos que había en aquella prisión; todo lo que se hacía allí, él lo hacía.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 39, 23, 'No necesitaba atender el jefe de la cárcel cosa alguna de las que estaban al cuidado de José, porque Jehová estaba con José, y lo que él hacía, Jehová lo prosperaba.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 1, 'Aconteció después de estas cosas, que el copero del rey de Egipto y el panadero delinquieron contra su señor el rey de Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 2, 'Y se enojó Faraón contra sus dos oficiales, contra el jefe de los coperos y contra el jefe de los panaderos,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 3, 'y los puso en prisión en la casa del capitán de la guardia, en la cárcel donde José estaba preso.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 4, 'Y el capitán de la guardia encargó de ellos a José, y él les servía; y estuvieron días en la prisión.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 5, 'Y ambos, el copero y el panadero del rey de Egipto, que estaban arrestados en la prisión, tuvieron un sueño, cada uno su propio sueño en una misma noche, cada uno con su propio significado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 6, 'Vino a ellos José por la mañana, y los miró, y he aquí que estaban tristes.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 7, 'Y él preguntó a aquellos oficiales de Faraón, que estaban con él en la prisión de la casa de su señor, diciendo: ¿Por qué parecen hoy mal vuestros semblantes?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 8, 'Ellos le dijeron: Hemos tenido un sueño, y no hay quien lo interprete. Entonces les dijo José: ¿No son de Dios las interpretaciones? Contádmelo ahora.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 9, 'Entonces el jefe de los coperos contó su sueño a José, y le dijo: Yo soñaba que veía una vid delante de mí,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 10, 'y en la vid tres sarmientos; y ella como que brotaba, y arrojaba su flor, viniendo a madurar sus racimos de uvas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 11, 'Y que la copa de Faraón estaba en mi mano, y tomaba yo las uvas y las exprimía en la copa de Faraón, y daba yo la copa en mano de Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 12, 'Y le dijo José: Esta es su interpretación: los tres sarmientos son tres días.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 13, 'Al cabo de tres días levantará Faraón tu cabeza, y te restituirá a tu puesto, y darás la copa a Faraón en su mano, como solías hacerlo cuando eras su copero.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 14, 'Acuérdate, pues, de mí cuando tengas ese bien, y te ruego que uses conmigo de misericordia, y hagas mención de mí a Faraón, y me saques de esta casa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 15, 'Porque fui hurtado de la tierra de los hebreos; y tampoco he hecho aquí por qué me pusiesen en la cárcel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 16, 'Viendo el jefe de los panaderos que había interpretado para bien, dijo a José: También yo soñé que veía tres canastillos blancos sobre mi cabeza.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 17, 'En el canastillo más alto había de toda clase de manjares de pastelería para Faraón; y las aves las comían del canastillo de sobre mi cabeza.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 18, 'Entonces respondió José, y dijo: Esta es su interpretación: Los tres canastillos tres días son.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 19, 'Al cabo de tres días quitará Faraón tu cabeza de sobre ti, y te hará colgar en la horca, y las aves comerán tu carne de sobre ti.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 20, 'Al tercer día, que era el día del cumpleaños de Faraón, el rey hizo banquete a todos sus sirvientes; y alzó la cabeza del jefe de los coperos, y la cabeza del jefe de los panaderos, entre sus servidores.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 21, 'E hizo volver a su oficio al jefe de los coperos, y dio éste la copa en mano de Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 22, 'Mas hizo ahorcar al jefe de los panaderos, como lo había interpretado José.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 40, 23, 'Y el jefe de los coperos no se acordó de José, sino que le olvidó.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 1, 'Vino José y lo hizo saber a Faraón, y dijo: Mi padre y mis hermanos, y sus ovejas y sus vacas, con todo lo que tienen, han venido de la tierra de Canaán, y he aquí están en la tierra de Gosén.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 2, 'Y de los postreros de sus hermanos tomó cinco varones, y los presentó delante de Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 3, 'Y Faraón dijo a sus hermanos: ¿Cuál es vuestro oficio? Y ellos respondieron a Faraón: Pastores de ovejas son tus siervos, así nosotros como nuestros padres.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 4, 'Dijeron además a Faraón: Para morar en esta tierra hemos venido; porque no hay pasto para las ovejas de tus siervos, pues el hambre es grave en la tierra de Canaán; por tanto, te rogamos ahora que permitas que habiten tus siervos en la tierra de Gosén.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 5, 'Entonces Faraón habló a José, diciendo: Tu padre y tus hermanos han venido a ti.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 6, 'La tierra de Egipto delante de ti está; en lo mejor de la tierra haz habitar a tu padre y a tus hermanos; habiten en la tierra de Gosén; y si entiendes que hay entre ellos hombres capaces, ponlos por mayorales del ganado mío.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 7, 'También José introdujo a Jacob su padre, y lo presentó delante de Faraón; y Jacob bendijo a Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 8, 'Y dijo Faraón a Jacob: ¿Cuántos son los días de los años de tu vida?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 9, 'Y Jacob respondió a Faraón: Los días de los años de mi peregrinación son ciento treinta años; pocos y malos han sido los días de los años de mi vida, y no han llegado a los días de los años de la vida de mis padres en los días de su peregrinación.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 10, 'Y Jacob bendijo a Faraón, y salió de la presencia de Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 11, 'Así José hizo habitar a su padre y a sus hermanos, y les dio posesión en la tierra de Egipto, en lo mejor de la tierra, en la tierra de Ramesés, como mandó Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 12, 'Y alimentaba José a su padre y a sus hermanos, y a toda la casa de su padre, con pan, según el número de los hijos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 13, 'No había pan en toda la tierra, y el hambre era muy grave, por lo que desfalleció de hambre la tierra de Egipto y la tierra de Canaán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 14, 'Y recogió José todo el dinero que había en la tierra de Egipto y en la tierra de Canaán, por los alimentos que de él compraban; y metió José el dinero en casa de Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 15, 'Acabado el dinero de la tierra de Egipto y de la tierra de Canaán, vino todo Egipto a José, diciendo: Danos pan; ¿por qué moriremos delante de ti, por haberse acabado el dinero?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 16, 'Y José dijo: Dad vuestros ganados y yo os daré por vuestros ganados, si se ha acabado el dinero.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 17, 'Y ellos trajeron sus ganados a José, y José les dio alimentos por caballos, y por el ganado de las ovejas, y por el ganado de las vacas, y por asnos; y les sustentó de pan por todos sus ganados aquel año.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 18, 'Acabado aquel año, vinieron a él el segundo año, y le dijeron: No encubrimos a nuestro señor que el dinero ciertamente se ha acabado; también el ganado es ya de nuestro señor; nada ha quedado delante de nuestro señor sino nuestros cuerpos y nuestra tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 19, '¿Por qué moriremos delante de tus ojos, así nosotros como nuestra tierra? Cómpranos a nosotros y a nuestra tierra por pan, y seremos nosotros y nuestra tierra siervos de Faraón; y danos semilla para que vivamos y no muramos, y no sea asolada la tierra. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 20, 'Entonces compró José toda la tierra de Egipto para Faraón; pues los egipcios vendieron cada uno sus tierras, porque se agravó el hambre sobre ellos; y la tierra vino a ser de Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 21, 'Y al pueblo lo hizo pasar a las ciudades, desde un extremo al otro del territorio de Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 22, 'Solamente la tierra de los sacerdotes no compró, por cuanto los sacerdotes tenían ración de Faraón, y ellos comían la ración que Faraón les daba; por eso no vendieron su tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 23, 'Y José dijo al pueblo: He aquí os he comprado hoy, a vosotros y a vuestra tierra, para Faraón; ved aquí semilla, y sembraréis la tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 24, 'De los frutos daréis el quinto a Faraón, y las cuatro partes serán vuestras para sembrar las tierras, y para vuestro mantenimiento, y de los que están en vuestras casas, y para que coman vuestros niños.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 25, 'Y ellos respondieron: La vida nos has dado; hallemos gracia en ojos de nuestro señor, y seamos siervos de Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 26, 'Entonces José lo puso por ley hasta hoy sobre la tierra de Egipto, señalando para Faraón el quinto, excepto sólo la tierra de los sacerdotes, que no fue de Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 27, 'Así habitó Israel en la tierra de Egipto, en la tierra de Gosén; y tomaron posesión de ella, y se aumentaron, y se multiplicaron en gran manera.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 28, 'Y vivió Jacob en la tierra de Egipto diecisiete años; y fueron los días de Jacob, los años de su vida, ciento cuarenta y siete años.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 29, 'Y llegaron los días de Israel para morir, y llamó a José su hijo, y le dijo: Si he hallado ahora gracia en tus ojos, te ruego que pongas tu mano debajo de mi muslo, y harás conmigo misericordia y verdad. Te ruego que no me entierres en Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 30, 'Mas cuando duerma con mis padres, me llevarás de Egipto y me sepultarás en el sepulcro de ellos. Y José respondió: Haré como tú dices.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 47, 31, 'E Israel dijo: Júramelo. Y José le juró. Entonces Israel se inclinó sobre la cabecera de la cama.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 1, 'Sucedió después de estas cosas que dijeron a José: He aquí tu padre está enfermo. Y él tomó consigo a sus dos hijos, Manasés y Efraín. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 2, 'Y se le hizo saber a Jacob, diciendo: He aquí tu hijo José viene a ti. Entonces se esforzó Israel, y se sentó sobre la cama');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 3, 'y dijo a José: El Dios Omnipotente me apareció en Luz en la tierra de Canaán, y me bendijo');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 4, 'y me dijo: He aquí yo te haré crecer, y te multiplicaré, y te pondré por estirpe de naciones; y daré esta tierra a tu descendencia después de ti por heredad perpetua.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 5, 'Y ahora tus dos hijos Efraín y Manasés, que te nacieron en la tierra de Egipto, antes que viniese a ti a la tierra de Egipto, míos son; como Rubén y Simeón, serán míos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 6, 'Y los que después de ellos has engendrado, serán tuyos; por el nombre de sus hermanos serán llamados en sus heredades.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 7, 'Porque cuando yo venía de Padan-aram, se me murió Raquel en la tierra de Canaán, en el camino, como media legua de tierra viniendo a Efrata; y la sepulté allí en el camino de Efrata, que es Belén.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 8, 'Y vio Israel los hijos de José, y dijo: ¿Quiénes son éstos?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 9, 'Y respondió José a su padre: Son mis hijos, que Dios me ha dado aquí. Y él dijo: Acércalos ahora a mí, y los bendeciré.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 10, 'Y los ojos de Israel estaban tan agravados por la vejez, que no podía ver. Les hizo, pues, acercarse a él, y él les besó y les abrazó.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 11, 'Y dijo Israel a José: No pensaba yo ver tu rostro, y he aquí Dios me ha hecho ver también a tu descendencia.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 12, 'Entonces José los sacó de entre sus rodillas, y se inclinó a tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 13, 'Y los tomó José a ambos, Efraín a su derecha, a la izquierda de Israel, y Manasés a su izquierda, a la derecha de Israel; y los acercó a él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 14, 'Entonces Israel extendió su mano derecha, y la puso sobre la cabeza de Efraín, que era el menor, y su mano izquierda sobre la cabeza de Manasés, colocando así sus manos adrede, aunque Manasés era el primogénito.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 15, 'Y bendijo a José, diciendo: El Dios en cuya presencia anduvieron mis padres Abraham e Isaac, el Dios que me mantiene desde que yo soy hasta este día,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 16, 'el Angel que me liberta de todo mal, bendiga a estos jóvenes; y sea perpetuado en ellos mi nombre, y el nombre de mis padres Abraham e Isaac, y multiplíquense en gran manera en medio de la tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 17, 'Pero viendo José que su padre ponía la mano derecha sobre la cabeza de Efraín, le causó esto disgusto; y asió la mano de su padre, para cambiarla de la cabeza de Efraín a la cabeza de Manasés.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 18, 'Y dijo José a su padre: No así, padre mío, porque éste es el primogénito; pon tu mano derecha sobre su cabeza.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 19, 'Mas su padre no quiso, y dijo: Lo sé, hijo mío, lo sé; también él vendrá a ser un pueblo, y será también engrandecido; pero su hermano menor será más grande que él, y su descendencia formará multitud de naciones.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 20, 'Y los bendijo aquel día, diciendo: En ti bendecirá Israel, diciendo: Hágate Dios como a Efraín y como a Manasés. Y puso a Efraín antes de Manasés.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 21, 'Y dijo Israel a José: He aquí yo muero; pero Dios estará con vosotros, y os hará volver a la tierra de vuestros padres.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 48, 22, '');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 1, 'Y llamó Jacob a sus hijos, y dijo: Juntaos, y os declararé lo que os ha de acontecer en los días venideros.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 2, 'Juntaos y oíd, hijos de Jacob, y escuchad a vuestro padre Israel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 3, 'Rubén, tú eres mi primogénito, mi fortaleza, y el principio de mi vigor; Principal en dignidad, principal en poder.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 4, 'Impetuoso como las aguas, no serás el principal, Por cuanto subiste al lecho de tu padre Entonces te envileciste, subiendo a mi estrado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 5, 'Simeón y Leví son hermanos; Armas de iniquidad sus armas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 6, '6 En su consejo no entre mi alma, Ni mi espíritu se junte en su compañía. Porque en su furor mataron hombres, Y en su temeridad desjarretaron toros. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 7, 'Maldito su furor, que fue fiero, Y su ira, que fue dura. Yo los apartaré en Jacob, Y los esparciré en Israel. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 8, 'Judá, te alabarán tus hermanos, Tu mano en la cerviz de tus enemigos, Los hijos de tu padre se inclinarán a ti. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 9, 'Cachorro de león, Judá, De la presa subiste, hijo mío. Se encorvó, se echó como león, Así como león viejo: ¿quién lo despertará? ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 10, 'No será quitado el cetro de Judá, Ni el legislador de entre sus pies, Hasta que venga Siloh, Y a él se congregarán los pueblos. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 11, 'Atando a la vid su pollino, Y a la cepa el hijo de su asna, Lavó en el vino su vestido, Y en la sangre de uvas su manto. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 12, 'Sus ojos, rojos del vino, Y sus dientes blancos de la leche. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 13, 'Zabulón en puertos de mar habitará, Será para puerto de naves, Y su límite hasta Sidón. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 14, '14 Isacar, asno fuerte Que se recuesta entre los apriscos; ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 15, 'Y vio que el descanso era bueno, y que la tierra era deleitosa, Y bajó su hombro para llevar, Y sirvió en tributo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 16, 'Dan juzgará a su pueblo, Como una de las tribus de Israel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 17, 'Será Dan serpiente junto al camino, Víbora junto a la senda, Que muerde los talones del caballo, Y hace caer hacia atrás al jinete. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 18, 'Tu salvación esperé, oh Jehová');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 19, 'Gad, ejército lo acometerá; Mas él acometerá al fin.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 20, 'El pan de Aser será substancioso, Y él dará deleites al rey.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 21, 'Neftalí, cierva suelta, Que pronunciará dichos hermosos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 22, 'Rama fructífera es José, Rama fructífera junto a una fuente, Cuyos vástagos se extienden sobre el muro.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 23, 'Le causaron amargura, Le asaetearon, Y le aborrecieron los arqueros');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 24, 'Mas su arco se mantuvo poderoso, Y los brazos de sus manos se fortalecieron, Por las manos del Fuerte de Jacob (Por el nombre del Pastor, la Roca de Israel),');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 25, 'Por el Dios de tu padre, el cual te ayudará, Por el Dios Omnipotente, el cual te bendecirá Con bendiciones de los cielos de arriba, Con bendiciones del abismo que está abajo, Con bendiciones de los pechos y del vientre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 26, 'Las bendiciones de tu padre Fueron mayores que las bendiciones de mis progenitores, Hasta el término de los collados eternos Serán sobre la cabeza de José, Y sobre la frente del que fue apartado de entre sus hermanos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 27, 'Benjamín es lobo arrebatador; A la mañana comerá la presa, Y a la tarde repartirá los despojos. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 28, 'Todos éstos fueron las doce tribus de Israel, y esto fue lo que su padre les dijo, al bendecirlos; a cada uno por su bendición los bendijo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 29, 'Les mandó luego, y les dijo: Yo voy a ser reunido con mi pueblo. Sepultadme con mis padres en la cueva que está en el campo de Efrón el heteo,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 30, 'en la cueva que está en el campo de Macpela, al oriente de Mamre en la tierra de Canaán, la cual compró Abraham con el mismo campo de Efrón el heteo, para heredad de sepultura.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 31, 'Allí sepultaron a Abraham y a Sara su mujer; allí sepultaron a Isaac y a Rebeca su mujer; allí también sepulté yo a Lea.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 32, 'La compra del campo y de la cueva que está en él, fue de los hijos de Het.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 49, 33, 'Y cuando acabó Jacob de dar mandamientos a sus hijos, encogió sus pies en la cama, y expiró, y fue reunido con sus padres.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 1, 'Jacob siguió su camino, y le salieron al encuentro ángeles de Dios.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 2, 'Y dijo Jacob cuando los vio: Campamento de Dios es este; y llamó el nombre de aquel lugar Mahanaim.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 3, 'Y envió Jacob mensajeros delante de sí a Esaú su hermano, a la tierra de Seir, campo de Edom.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 4, 'Y les mandó diciendo: Así diréis a mi señor Esaú: Así dice tu siervo Jacob: Con Labán he morado, y me he detenido hasta ahora;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 5, 'y tengo vacas, asnos, ovejas, y siervos y siervas; y envío a decirlo a mi señor, para hallar gracia en tus ojos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 6, 'Y los mensajeros volvieron a Jacob, diciendo: Vinimos a tu hermano Esaú, y él también viene a recibirte, y cuatrocientos hombres con él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 7, 'Entonces Jacob tuvo gran temor, y se angustió; y distribuyó el pueblo que tenía consigo, y las ovejas y las vacas y los camellos, en dos campamentos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 8, 'Y dijo: Si viene Esaú contra un campamento y lo ataca, el otro campamento escapará.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 9, 'Y dijo Jacob: Dios de mi padre Abraham, y Dios de mi padre Isaac, Jehová, que me dijiste: Vuélvete a tu tierra y a tu parentela, y yo te haré bien; ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 10, '10 menor soy que todas las misericordias y que toda la verdad que has usado para con tu siervo; pues con mi cayado pasé este Jordán, y ahora estoy sobre dos campamentos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 11, 'Líbrame ahora de la mano de mi hermano, de la mano de Esaú, porque le temo; no venga acaso y me hiera la madre con los hijos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 12, 'Y tú has dicho: Yo te haré bien, y tu descendencia será como la arena del mar, que no se puede contar por la multitud.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 13, 'Y durmió allí aquella noche, y tomó de lo que le vino a la mano un presente para su hermano Esaú: ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 14, 'doscientas cabras y veinte machos cabríos, doscientas ovejas y veinte carneros,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 15, 'treinta camellas paridas con sus crías, cuarenta vacas y diez novillos, veinte asnas y diez borricos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 16, 'Y lo entregó a sus siervos, cada manada de por sí; y dijo a sus siervos: Pasad delante de mí, y poned espacio entre manada y manada.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 17, 'Y mandó al primero, diciendo: Si Esaú mi hermano te encontrare, y te preguntare, diciendo: ¿De quién eres? ¿y adónde vas? ¿y para quién es esto que llevas delante de ti? ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 18, 'entonces dirás: Es un presente de tu siervo Jacob, que envía a mi señor Esaú; y he aquí también él viene tras nosotros.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 19, 'Mandó también al segundo, y al tercero, y a todos los que iban tras aquellas manadas, diciendo: Conforme a esto hablaréis a Esaú, cuando le hallareis.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 20, 'Y diréis también: He aquí tu siervo Jacob viene tras nosotros. Porque dijo: Apaciguaré su ira con el presente que va delante de mí, y después veré su rostro; quizá le seré acepto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 21, 'Pasó, pues, el presente delante de él; y él durmió aquella noche en el campamento.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 22, 'Y se levantó aquella noche, y tomó sus dos mujeres, y sus dos siervas, y sus once hijos, y pasó el vado de Jaboc.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 23, 'Los tomó, pues, e hizo pasar el arroyo a ellos y a todo lo que tenía.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 24, 'Así se quedó Jacob solo; y luchó con él un varón hasta que rayaba el alba. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 25, 'Y cuando el varón vio que no podía con él, tocó en el sitio del encaje de su muslo, y se descoyuntó el muslo de Jacob mientras con él luchaba.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 26, 'Y dijo: Déjame, porque raya el alba. Y Jacob le respondió: No te dejaré, si no me bendices.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 27, 'Y el varón le dijo: ¿Cuál es tu nombre? Y él respondió: Jacob.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 28, 'Y el varón le dijo: No se dirá más tu nombre Jacob, sino Israel; porque has luchado con Dios y con los hombres, y has vencido.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 29, 'Entonces Jacob le preguntó, y dijo: Declárame ahora tu nombre. Y el varón respondió: ¿Por qué me preguntas por mi nombre? Y lo bendijo allí.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 30, 'Y llamó Jacob el nombre de aquel lugar, Peniel; porque dijo: Vi a Dios cara a cara, y fue librada mi alma.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 31, 'Y cuando había pasado Peniel, le salió el sol; y cojeaba de su cadera.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 32, 32, 'Por esto no comen los hijos de Israel, hasta hoy día, del tendón que se contrajo, el cual está en el encaje del muslo; porque tocó a Jacob este sitio de su muslo en el tendón que se contrajo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 1, 'Alzando Jacob sus ojos, miró, y he aquí venía Esaú, y los cuatrocientos hombres con él; entonces repartió él los niños entre Lea y Raquel y las dos siervas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 2, 'Y puso las siervas y sus niños delante, luego a Lea y sus niños, y a Raquel y a José los últimos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 3, 'Y él pasó delante de ellos y se inclinó a tierra siete veces, hasta que llegó a su hermano.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 4, 'Pero Esaú corrió a su encuentro y le abrazó, y se echó sobre su cuello, y le besó; y lloraron.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 5, 'Y alzó sus ojos y vio a las mujeres y los niños, y dijo: ¿Quiénes son éstos? Y él respondió: Son los niños que Dios ha dado a tu siervo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 6, 'Luego vinieron las siervas, ellas y sus niños, y se inclinaron.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 7, 'Y vino Lea con sus niños, y se inclinaron; y después llegó José y Raquel, y también se inclinaron.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 8, 'Y Esaú dijo: ¿Qué te propones con todos estos grupos que he encontrado? Y Jacob respondió: El hallar gracia en los ojos de mi señor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 9, 'Y dijo Esaú: Suficiente tengo yo, hermano mío; sea para ti lo que es tuyo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 10, 'Y dijo Jacob: No, yo te ruego; si he hallado ahora gracia en tus ojos, acepta mi presente, porque he visto tu rostro, como si hubiera visto el rostro de Dios, pues que con tanto favor me has recibido.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 11, 'Acepta, te ruego, mi presente que te he traído, porque Dios me ha hecho merced, y todo lo que hay aquí es mío. E insistió con él, y Esaú lo tomó.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 12, 'Y Esaú dijo: Anda, vamos; y yo iré delante de ti.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 13, 'Y Jacob le dijo: Mi señor sabe que los niños son tiernos, y que tengo ovejas y vacas paridas; y si las fatigan, en un día morirán todas las ovejas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 14, 'Pase ahora mi señor delante de su siervo, y yo me iré poco a poco al paso del ganado que va delante de mí y al paso de los niños, hasta que llegue a mi señor a Seir.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 15, 'Y Esaú dijo: Dejaré ahora contigo de la gente que viene conmigo. Y Jacob dijo: ¿Para qué esto? Halle yo gracia en los ojos de mi señor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 16, 'Así volvió Esaú aquel día por su camino a Seir.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 17, 'Y Jacob fue a Sucot, y edificó allí casa para sí, e hizo cabañas para su ganado; por tanto, llamó el nombre de aquel lugar Sucot.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 18, 'Después Jacob llegó sano y salvo a la ciudad de Siquem, que está en la tierra de Canaán, cuando venía de Padan-aram; y acampó delante de la ciudad.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 19, 'Y compró una parte del campo, donde plantó su tienda, de mano de los hijos de Hamor padre de Siquem, por cien monedas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 33, 20, 'Y erigió allí un altar, y lo llamó El-Elohe-Israel.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 1,'Salió Dina la hija de Lea, la cual ésta había dado a luz a Jacob, a ver a las hijas del país.'); 
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 2,'Y la vio Siquem hijo de Hamor heveo, príncipe de aquella tierra, y la tomó, y se acostó con ella, y la deshonró.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 3,'Pero su alma se apegó a Dina la hija de Lea, y se enamoró de la joven, y habló al corazón de ella.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 4,'Y habló Siquem a Hamor su padre, diciendo: Tómame por mujer a esta joven.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 5,'Pero oyó Jacob que Siquem había amancillado a Dina su hija; y estando sus hijos con su ganado en el campo, calló Jacob hasta que ellos viniesen.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 6 ,'Y se dirigió Hamor padre de Siquem a Jacob, para hablar con él.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 7,'Y los hijos de Jacob vinieron del campo cuando lo supieron; y se entristecieron los varones, y se enojaron mucho, porque hizo vileza en Israel acostándose con la hija de Jacob, lo que no se debía haber hecho.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 8,'Y Hamor habló con ellos, diciendo: El alma de mi hijo Siquem se ha apegado a vuestra hija; os ruego que se la deis por mujer.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 9,'Y emparentad con nosotros; dadnos vuestras hijas, y tomad vosotros las nuestras.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 10,'Y habitad con nosotros, porque la tierra estará delante de vosotros; morad y negociad en ella, y tomad en ella posesión.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 11,'Siquem también dijo al padre de Dina y a los hermanos de ella: Halle yo gracia en vuestros ojos, y daré lo que me dijereis.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 12,'Aumentad a cargo mío mucha dote y dones, y yo daré cuanto me dijereis; y dadme la joven por mujer.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 13,'Pero respondieron los hijos de Jacob a Siquem y a Hamor su padre con palabras engañosas, por cuanto había amancillado a Dina su hermana.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 14,'Y les dijeron: No podemos hacer esto de dar nuestra hermana a hombre incircunciso, porque entre nosotros es abominación.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 15,'Mas con esta condición os complaceremos: si habéis de ser como nosotros, que se circuncide entre vosotros todo varón.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 16,'Entonces os daremos nuestras hijas, y tomaremos nosotros las vuestras; y habitaremos con vosotros, y seremos un pueblo.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 17,'Mas si no nos prestareis oído para circuncidaros, tomaremos nuestra hija y nos iremos.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 18,'Y parecieron bien sus palabras a Hamor, y a Siquem hijo de Hamor.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 19,'Y no tardó el joven en hacer aquello, porque la hija de Jacob le había agradado; y él era el más distinguido de toda la casa de su padre.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 20,'Entonces Hamor y Siquem su hijo vinieron a la puerta de su ciudad, y hablaron a los varones de su ciudad, diciendo:');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 21, 'Estos varones son pacíficos con nosotros, y habitarán en el país, y traficarán en él; pues he aquí la tierra es bastante ancha para ellos; nosotros tomaremos sus hijas por mujeres, y les daremos las nuestras.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 22,'Mas con esta condición consentirán estos hombres en habitar con nosotros, para que seamos un pueblo: que se circuncide todo varón entre nosotros, así como ellos son circuncidados.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 23, 'Su ganado, sus bienes y todas sus bestias serán nuestros; solamente convengamos con ellos, y habitarán con nosotros.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 24, 'Y obedecieron a Hamor y a Siquem su hijo todos los que salían por la puerta de la ciudad, y circuncidaron a todo varón, a cuantos salían por la puerta de su ciudad.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 25, 'Pero sucedió que al tercer día, cuando sentían ellos el mayor dolor, dos de los hijos de Jacob, Simeón y Leví, hermanos de Dina, tomaron cada uno su espada, y vinieron contra la ciudad, que estaba desprevenida, y mataron a todo varón.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 26, 'Y a Hamor y a Siquem su hijo los mataron a filo de espada; y tomaron a Dina de casa de Siquem, y se fueron.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 27, 'Y los hijos de Jacob vinieron a los muertos, y saquearon la ciudad, por cuanto habían amancillado a su hermana.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 28, 'Tomaron sus ovejas y vacas y sus asnos, y lo que había en la ciudad y en el campo,');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 29, 'y todos sus bienes; llevaron cautivos a todos sus niños y sus mujeres, y robaron todo lo que había en casa.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 30, 'Entonces dijo Jacob a Simeón y a Leví: Me habéis turbado con hacerme abominable a los moradores de esta tierra, el cananeo y el ferezeo; y teniendo yo pocos hombres, se juntarán contra mí y me atacarán, y seré destruido yo y mi casa.');
Insert into Versiculos(Id_Version,Id_libro,NumeroCap,NumeroVers,Texto) values (1, 1, 34, 31, 'Pero ellos respondieron: ¿Había él de tratar a nuestra hermana como a una ramera? Dios bendice a Jacob en Bet-el');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 1, 'Mandó José al mayordomo de su casa, diciendo: Llena de alimento los costales de estos varones, cuanto puedan llevar, y pon el dinero de cada uno en la boca de su costal.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 2, 'Y pondrás mi copa, la copa de plata, en la boca del costal del menor, con el dinero de su trigo. Y él hizo como dijo José.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 3, 'Venida la mañana, los hombres fueron despedidos con sus asnos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 4, 'Habiendo ellos salido de la ciudad, de la que aún no se habían alejado, dijo José a su mayordomo: Levántate y sigue a esos hombres; y cuando los alcances, diles: ¿Por qué habéis vuelto mal por bien? ¿Por qué habéis robado mi copa de plata?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 5, '¿No es ésta en la que bebe mi señor, y por la que suele adivinar? Habéis hecho mal en lo que hicisteis.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 6, 'Cuando él los alcanzó, les dijo estas palabras.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 7, 'Y ellos le respondieron: ¿Por qué dice nuestro señor tales cosas? Nunca tal hagan tus siervos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 8, 'He aquí, el dinero que hallamos en la boca de nuestros costales, te lo volvimos a traer desde la tierra de Canaán; ¿cómo, pues, habíamos de hurtar de casa de tu señor plata ni oro?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 9, 'Aquel de tus siervos en quien fuere hallada la copa, que muera, y aun nosotros seremos siervos de mi señor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 10, 'Y él dijo: También ahora sea conforme a vuestras palabras; aquel en quien se hallare será mi siervo, y vosotros seréis sin culpa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 11, 'Ellos entonces se dieron prisa, y derribando cada uno su costal en tierra, abrió cada cual el costal suyo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 12, 'Y buscó; desde el mayor comenzó, y acabó en el menor; y la copa fue hallada en el costal de Benjamín.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 13, 'Entonces ellos rasgaron sus vestidos, y cargó cada uno su asno y volvieron a la ciudad.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 14, 'Vino Judá con sus hermanos a casa de José, que aún estaba allí, y se postraron delante de él en tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 15, 'Y les dijo José: ¿Qué acción es esta que habéis hecho? ¿No sabéis que un hombre como yo sabe adivinar?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 16, 'Entonces dijo Judá: ¿Qué diremos a mi señor? ¿Qué hablaremos, o con qué nos justificaremos? Dios ha hallado la maldad de tus siervos; he aquí, nosotros somos siervos de mi señor, nosotros, y también aquel en cuyo poder fue hallada la copa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 17, 'José respondió: Nunca yo tal haga. El varón en cuyo poder fue hallada la copa, él será mi siervo; vosotros id en paz a vuestro padre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 18, 'Entonces Judá se acercó a él, y dijo: Ay, señor mío, te ruego que permitas que hable tu siervo una palabra en oídos de mi señor, y no se encienda tu enojo contra tu siervo, pues tú eres como Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 19, 'Mi señor preguntó a sus siervos, diciendo: ¿Tenéis padre o hermano?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 20, 'Y nosotros respondimos a mi señor: Tenemos un padre anciano, y un hermano joven, pequeño aún, que le nació en su vejez; y un hermano suyo murió, y él solo quedó de los hijos de su madre; y su padre lo ama.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 21, 'Y tú dijiste a tus siervos: Traédmelo, y pondré mis ojos sobre él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 22, 'Y nosotros dijimos a mi señor: El joven no puede dejar a su padre, porque si lo dejare, su padre morirá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 23, 'Y dijiste a tus siervos: Si vuestro hermano menor no desciende con vosotros, no veréis más mi rostro.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 24, 'Aconteció, pues, que cuando llegamos a mi padre tu siervo, le contamos las palabras de mi señor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 25, 'Y dijo nuestro padre: Volved a comprarnos un poco de alimento.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 26, 'Y nosotros respondimos: No podemos ir; si nuestro hermano va con nosotros, iremos; porque no podremos ver el rostro del varón, si no está con nosotros nuestro hermano el menor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 27, 'Entonces tu siervo mi padre nos dijo: Vosotros sabéis que dos hijos me dio a luz mi mujer;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 28, 'y el uno salió de mi presencia, y pienso de cierto que fue despedazado, y hasta ahora no lo he visto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 29, 'Y si tomáis también a éste de delante de mí, y le acontece algún desastre, haréis descender mis canas con dolor al Seol.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 30, 'Ahora, pues, cuando vuelva yo a tu siervo mi padre, si el joven no va conmigo, como su vida está ligada a la vida de él,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 31, 'sucederá que cuando no vea al joven, morirá; y tus siervos harán descender las canas de tu siervo nuestro padre con dolor al Seol.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 32, 'Como tu siervo salió por fiador del joven con mi padre, diciendo: Si no te lo vuelvo a traer, entonces yo seré culpable ante mi padre para siempre;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 33, 'te ruego, por tanto, que quede ahora tu siervo en lugar del joven por siervo de mi señor, y que el joven vaya con sus hermanos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 44, 34, 'Porque ¿cómo volveré yo a mi padre sin el joven? No podré, por no ver el mal que sobrevendrá a mi padre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 1, 'No podía ya José contenerse delante de todos los que estaban al lado suyo, y clamó: Haced salir de mi presencia a todos. Y no quedó nadie con él, al darse a conocer José a sus hermanos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 2, 'Entonces se dio a llorar a gritos; y oyeron los egipcios, y oyó también la casa de Faraón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 3, 'Y dijo José a sus hermanos: Yo soy José; ¿vive aún mi padre? Y sus hermanos no pudieron responderle, porque estaban turbados delante de él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 4, 'Entonces dijo José a sus hermanos: Acercaos ahora a mí. Y ellos se acercaron. Y él dijo: Yo soy José vuestro hermano, el que vendisteis para Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 5, 'Ahora, pues, no os entristezcáis, ni os pese de haberme vendido acá; porque para preservación de vida me envió Dios delante de vosotros.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 6, 'Pues ya ha habido dos años de hambre en medio de la tierra, y aún quedan cinco años en los cuales ni habrá arada ni siega.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 7, 'Y Dios me envió delante de vosotros, para preservaros posteridad sobre la tierra, y para daros vida por medio de gran liberación.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 8, 'Así, pues, no me enviasteis acá vosotros, sino Dios, que me ha puesto por padre de Faraón y por señor de toda su casa, y por gobernador en toda la tierra de Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 9, 'Daos prisa, id a mi padre y decidle: Así dice tu hijo José: Dios me ha puesto por señor de todo Egipto; ven a mí, no te detengas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 10, 'Habitarás en la tierra de Gosén, y estarás cerca de mí, tú y tus hijos, y los hijos de tus hijos, tus ganados y tus vacas, y todo lo que tienes.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 11, 'Y allí te alimentaré, pues aún quedan cinco años de hambre, para que no perezcas de pobreza tú y tu casa, y todo lo que tienes.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 12, 'He aquí, vuestros ojos ven, y los ojos de mi hermano Benjamín, que mi boca os habla.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 13, 'Haréis, pues, saber a mi padre toda mi gloria en Egipto, y todo lo que habéis visto; y daos prisa, y traed a mi padre acá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 14, 'Y se echó sobre el cuello de Benjamín su hermano, y lloró; y también Benjamín lloró sobre su cuello.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 15, 'Y besó a todos sus hermanos, y lloró sobre ellos; y después sus hermanos hablaron con él. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 16, 'Y se oyó la noticia en la casa de Faráon, diciendo: Los hermanos de José han venido. Y esto agradó en los ojos de Faraón y de sus siervos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 17, 'Y dijo Faraón a José: Di a tus hermanos: Haced esto: cargad vuestras bestias, e id, volved a la tierra de Canaán;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 18, 'y tomad a vuestro padre y a vuestras familias y venid a mí, porque yo os daré lo bueno de la tierra de Egipto, y comeréis de la abundancia de la tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 19, 'Y tú manda: Haced esto: tomaos de la tierra de Egipto carros para vuestros niños y vuestras mujeres, y traed a vuestro padre, y venid.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 20, 'Y no os preocupéis por vuestros enseres, porque la riqueza de la tierra de Egipto será vuestra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 21, 'Y lo hicieron así los hijos de Israel; y les dio José carros conforme a la orden de Faraón, y les suministró víveres para el camino.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 22, 'A cada uno de todos ellos dio mudas de vestidos, y a Benjamín dio trescientas piezas de plata, y cinco mudas de vestidos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 23, 'Y a su padre envió esto: diez asnos cargados de lo mejor de Egipto, y diez asnas cargadas de trigo, y pan y comida, para su padre en el camino.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 24, 'Y despidió a sus hermanos, y ellos se fueron. Y él les dijo: No riñáis por el camino.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 25, 'Y subieron de Egipto, y llegaron a la tierra de Canaán a Jacob su padre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 26, 'Y le dieron las nuevas, diciendo: José vive aún; y él es señor en toda la tierra de Egipto. Y el corazón de Jacob se afligió, porque no los creía.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 27, 'Y ellos le contaron todas las palabras de José, que él les había hablado; y viendo Jacob los carros que José enviaba para llevarlo, su espíritu revivió.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 45, 28, 'Entonces dijo Israel: Basta; José mi hijo vive todavía; iré, y le veré antes que yo muera.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 1, 'Salió Israel con todo lo que tenía, y vino a Beerseba, y ofreció sacrificios al Dios de su padre Isaac.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 2, 'Y habló Dios a Israel en visiones de noche, y dijo: Jacob, Jacob. Y él respondió: Heme aquí.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 3, 'Y dijo: Yo soy Dios, el Dios de tu padre; no temas de descender a Egipto, porque allí yo haré de ti una gran nación.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 4, 'Yo descenderé contigo a Egipto, y yo también te haré volver; y la mano de José cerrará tus ojos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 5, 'Y se levantó Jacob de Beerseba; y tomaron los hijos de Israel a su padre Jacob, y a sus niños, y a sus mujeres, en los carros que Faraón había enviado para llevarlo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 6, 'Y tomaron sus ganados, y sus bienes que habían adquirido en la tierra de Canaán, y vinieron a Egipto, Jacob y toda su descendencia consigo;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 7, 'sus hijos, y los hijos de sus hijos consigo; sus hijas, y las hijas de sus hijos, y a toda su descendencia trajo consigo a Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 8, 'Y estos son los nombres de los hijos de Israel, que entraron en Egipto, Jacob y sus hijos: Rubén, el primogénito de Jacob.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 9, 'Y los hijos de Rubén: Hanoc, Falú, Hezrón y Carmi.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 10, 'Los hijos de Simeón: Jemuel, Jamín, Ohad, Jaquín, Zohar, y Saúl hijo de la cananea.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 11, 'Los hijos de Leví: Gersón, Coat y Merari.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 12, 'Los hijos de Judá: Er, Onán, Sela, Fares y Zara; mas Er y Onán murieron en la tierra de Canaán. Y los hijos de Fares fueron Hezrón y Hamul.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 13, 'Los hijos de Isacar: Tola, Fúa, Job y Simrón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 14, 'Los hijos de Zabulón: Sered, Elón y Jahleel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 15, 'Estos fueron los hijos de Lea, los que dio a luz a Jacob en Padan-aram, y además su hija Dina; treinta y tres las personas todas de sus hijos e hijas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 16, 'Los hijos de Gad: Zifión, Hagui, Ezbón, Suni, Eri, Arodi y Areli.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 17, 'Y los hijos de Aser: Imna, Isúa, Isúi, Bería, y Sera hermana de ellos. Los hijos de Bería: Heber y Malquiel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 18, 'Estos fueron los hijos de Zilpa, la que Labán dio a su hija Lea, y dio a luz éstos a Jacob; por todas dieciséis personas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 19, 'Los hijos de Raquel, mujer de Jacob: José y Benjamín.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 20, 'Y nacieron a José en la tierra de Egipto Manasés y Efraín, los que le dio a luz Asenat, hija de Potifera sacerdote de On.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 21, 'Los hijos de Benjamín fueron Bela, Bequer, Asbel, Gera, Naamán, Ehi, Ros, Mupim, Hupim y Ard.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 22, 'Estos fueron los hijos de Raquel, que nacieron a Jacob; por todas catorce personas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 23, 'Los hijos de Dan: Husim.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 24, 'Los hijos de Neftalí: Jahzeel, Guni, Jezer y Silem.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 25, 'Estos fueron los hijos de Bilha, la que dio Labán a Raquel su hija, y dio a luz éstos a Jacob; por todas siete personas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 26, 'Todas las personas que vinieron con Jacob a Egipto, procedentes de sus lomos, sin las mujeres de los hijos de Jacob, todas las personas fueron sesenta y seis.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 27, 'Y los hijos de José, que le nacieron en Egipto, dos personas. Todas las personas de la casa de Jacob, que entraron en Egipto, fueron setenta.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 28, 'Y envió Jacob a Judá delante de sí a José, para que le viniese a ver en Gosén; y llegaron a la tierra de Gosén.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 29, 'Y José unció su carro y vino a recibir a Israel su padre en Gosén; y se manifestó a él, y se echó sobre su cuello, y lloró sobre su cuello largamente.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 30, 'Entonces Israel dijo a José: Muera yo ahora, ya que he visto tu rostro, y sé que aún vives.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 31, 'Y José dijo a sus hermanos, y a la casa de su padre: Subiré y lo haré saber a Faraón, y le diré: Mis hermanos y la casa de mi padre, que estaban en la tierra de Canaán, han venido a mí.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 32, 'Y los hombres son pastores de ovejas, porque son hombres ganaderos; y han traído sus ovejas y sus vacas, y todo lo que tenían.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 33, 'Y cuando Faraón os llamare y dijere: ¿Cuál es vuestro oficio?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 46, 34, 'entonces diréis: Hombres de ganadería han sido tus siervos desde nuestra juventud hasta ahora, nosotros y nuestros padres; a fin de que moréis en la tierra de Gosén, porque para los egipcios es abominación todo pastor de ovejas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 1, 'Habló Jehová a Moisés, diciendo:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 2, 'Mira, yo he llamado por nombre a Bezaleel hijo de Uri, hijo de Hur, de la tribu de Judá;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 3, ' Y lo he llenado del Espíritu de Dios, en sabiduría y en inteligencia, en ciencia y en todo arte,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 4, 'Para inventar diseños, para trabajar en oro, en plata y en bronce,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 5, 'y en artificio de piedras para engastarlas, y en artificio de madera; para trabajar en toda clase de labor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 6, 'Y he aquí que yo he puesto con él a Aholiab hijo de Ahisamac, de la tribu de Dan; y he puesto sabiduría en el ánimo de todo sabio de corazón, para que hagan todo lo que te he mandado; ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 7, 'El tabernáculo de reunión, el arca del testimonio, el propiciatorio que está sobre ella, y todos los utensilios del tabernáculo, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 8, 'La mesa y sus utensilios, el candelero limpio y todos sus utensilios, el altar del incienso,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 9, 'El altar del holocausto y todos sus utensilios, la fuente y su base,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 10, 'Los vestidos del servicio, las vestiduras santas para Aarón el sacerdote, las vestiduras de sus hijos para que ejerzan el sacerdocio, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 11, 'El aceite de la unción, y el incienso aromático para el santuario; harán conforme a todo lo que te he mandado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 12, 'Habló además Jehová a Moisés, diciendo: ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 13, 'Tú hablarás a los hijos de Israel, diciendo: En verdad vosotros guardaréis mis días de reposo; porque es señal entre mí y vosotros por vuestras generaciones, para que sepáis que yo soy Jehová que os santifico. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 14, 'Así que guardaréis el día de reposo,porque santo es a vosotros; el que lo profanare, de cierto morirá; porque cualquiera que hiciere obra alguna en él, aquella persona será cortada de en medio de su pueblo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 15, 'Seis días se trabajará, mas el día séptimo es día de reposo consagrado a Jehová; cualquiera que trabaje en el día de reposo, ciertamente morirá. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 16, 'Guardarán, pues, el día de reposo los hijos de Israel, celebrándolo por sus generaciones por pacto perpetuo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 17, 'Señal es para siempre entre mí y los hijos de Israel; porque en seis días hizo Jehová los cielos y la tierra, y en el séptimo día cesó y reposó. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 31, 18, 'Y dio a Moisés, cuando acabó de hablar con él en el monte de Sinaí, dos tablas del testimonio, tablas de piedra escritas con el dedo de Dios.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 1, 'Viendo el pueblo que Moisés tardaba en descender del monte, se acercaron entonces a Aarón, y le dijeron: Levántate, haznos dioses que vayan delante de nosotros; porque a este Moisés, el varón que nos sacó de la tierra de Egipto, no sabemos qué le haya acontecido. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 2, 'Y Aarón les dijo: Apartad los zarcillos de oro que están en las orejas de vuestras mujeres, de vuestros hijos y de vuestras hijas, y traédmelos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 3, 'Entonces todo el pueblo apartó los zarcillos de oro que tenían en sus orejas, y los trajeron a Aarón; ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 4, 'y él los tomó de las manos de ellos, y le dio forma con buril, e hizo de ello un becerro de fundición. Entonces dijeron: Israel, estos son tus dioses, que te sacaron de la tierra de Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 5, 'Y viendo esto Aarón, edificó un altar delante del becerro; y pregonó Aarón, y dijo: Mañana será fiesta para Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 6, 'Y al día siguiente madrugaron, y ofrecieron holocaustos, y presentaron ofrendas de paz; y se sentó el pueblo a comer y a beber, y se levantó a regocijarse.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 7, 'Entonces Jehová dijo a Moisés: Anda, desciende, porque tu pueblo que sacaste de la tierra de Egipto se ha corrompido. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 8, 'Pronto se han apartado del camino que yo les mandé; se han hecho un becerro de fundición, y lo han adorado, y le han ofrecido sacrificios, y han dicho: Israel, estos son tus dioses, que te sacaron de la tierra de Egipto. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 9, 'Dijo más Jehová a Moisés: Yo he visto a este pueblo, que por cierto es pueblo de dura cerviz. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 10, 'Ahora, pues, déjame que se encienda mi ira en ellos, y los consuma; y de ti yo haré una nación grande. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 11, 'Entonces Moisés oró en presencia de Jehová su Dios, y dijo: Oh Jehová, ¿por qué se encenderá tu furor contra tu pueblo, que tú sacaste de la tierra de Egipto con gran poder y con mano fuerte? ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 12, '¿Por qué han de hablar los egipcios, diciendo: Para mal los sacó, para matarlos en los montes, y para raerlos de sobre la faz de la tierra? Vuélvete del ardor de tu ira, y arrepiéntete de este mal contra tu pueblo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 13, 'Acuérdate de Abraham, de Isaac y de Israel tus siervos, a los cuales has jurado por ti mismo, y les has dicho: Yo multiplicaré vuestra descendencia como las estrellas del cielo; y daré a vuestra descendencia toda esta tierra de que he hablado, y la tomarán por heredad para siempre. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 14, 'Entonces Jehová se arrepintió del mal que dijo que había de hacer a su pueblo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 15, 'Y volvió Moisés y descendió del monte, trayendo en su mano las dos tablas del testimonio, las tablas escritas por ambos lados; de uno y otro lado estaban escritas. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 16, 'Y las tablas eran obra de Dios, y la escritura era escritura de Dios grabada sobre las tablas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 17, 'Cuando oyó Josué el clamor del pueblo que gritaba, dijo a Moisés: Alarido de pelea hay en el campamento. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 18, 'Y él respondió: No es voz de alaridos de fuertes, ni voz de alaridos de débiles; voz de cantar oigo yo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 19, 'Y aconteció que cuando él llegó al campamento, y vio el becerro y las danzas, ardió la ira de Moisés, y arrojó las tablas de sus manos, y las quebró al pie del monte.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 20, 'Y tomó el becerro que habían hecho, y lo quemó en el fuego, y lo molió hasta reducirlo a polvo, que esparció sobre las aguas, y lo dio a beber a los hijos de Israel. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 21, 'Y dijo Moisés a Aarón: ¿Qué te ha hecho este pueblo, que has traído sobre él tan gran pecado? ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 22, 'Y respondió Aarón: No se enoje mi señor; tú conoces al pueblo, que es inclinado a mal. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 23, 'Porque me dijeron: Haznos dioses que vayan delante de nosotros; porque a este Moisés, el varón que nos sacó de la tierra de Egipto, no sabemos qué le haya acontecido.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 24, 'Y yo les respondí: ¿Quién tiene oro? Apartadlo. Y me lo dieron, y lo eché en el fuego, y salió este becerro. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 25, 'Y viendo Moisés que el pueblo estaba desenfrenado, porque Aarón lo había permitido, para vergüenza entre sus enemigos, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 26, 'se puso Moisés a la puerta del campamento, y dijo: ¿Quién está por Jehová? Júntese conmigo. Y se juntaron con él todos los hijos de Leví.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 27, '27 Y él les dijo: Así ha dicho Jehová, el Dios de Israel: Poned cada uno su espada sobre su muslo; pasad y volved de puerta a puerta por el campamento, y matad cada uno a su hermano, y a su amigo, y a su pariente.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 28, 'Y los hijos de Leví lo hicieron conforme al dicho de Moisés; y cayeron del pueblo en aquel día como tres mil hombres. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 29, 'Entonces Moisés dijo: Hoy os habéis consagrado a Jehová, pues cada uno se ha consagrado en su hijo y en su hermano, para que él dé bendición hoy sobre vosotros. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 30, 'Y aconteció que al día siguiente dijo Moisés al pueblo: Vosotros habéis cometido un gran pecado, pero yo subiré ahora a Jehová; quizá le aplacaré acerca de vuestro pecado. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 31, 'Entonces volvió Moisés a Jehová, y dijo: Te ruego, pues este pueblo ha cometido un gran pecado, porque se hicieron dioses de oro, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 32, 'que perdones ahora su pecado, y si no, ráeme ahora de tu libro que has escrito.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 33, 'Y Jehová respondió a Moisés: Al que pecare contra mí, a éste raeré yo de mi libro. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 34, 'Ve, pues, ahora, lleva a este pueblo a donde te he dicho; he aquí mi ángel irá delante de ti; pero en el día del castigo, yo castigaré en ellos su pecado. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 32, 35, 'Y Jehová hirió al pueblo, porque habían hecho el becerro que formó Aarón. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 1, 'Jehová dijo a Moisés: Anda, sube de aquí, tú y el pueblo que sacaste de la tierra de Egipto, a la tierra de la cual juré a Abraham, Isaac y Jacob, diciendo: A tu descendencia la daré; ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 2, 'y yo enviaré delante de ti el ángel, y echaré fuera al cananeo y al amorreo, al heteo, al ferezeo, al heveo y al jebuseo');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 3, '(a la tierra que fluye leche y miel); pero yo no subiré en medio de ti, porque eres pueblo de dura cerviz, no sea que te consuma en el camino.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 4, 'Y oyendo el pueblo esta mala noticia, vistieron luto, y ninguno se puso sus atavíos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 5, 'Porque Jehová había dicho a Moisés: Di a los hijos de Israel: Vosotros sois pueblo de dura cerviz; en un momento subiré en medio de ti, y te consumiré. Quítate, pues, ahora tus atavíos, para que yo sepa lo que te he de hacer.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 6, 'Entonces los hijos de Israel se despojaron de sus atavíos desde el monte Horeb.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 7, 'Y Moisés tomó el tabernáculo, y lo levantó lejos, fuera del campamento, y lo llamó el Tabernáculo de Reunión. Y cualquiera que buscaba a Jehová, salía al tabernáculo de reunión que estaba fuera del campamento.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 8, 'Y sucedía que cuando salía Moisés al tabernáculo, todo el pueblo se levantaba, y cada cual estaba en pie a la puerta de su tienda, y miraban en pos de Moisés, hasta que él entraba en el tabernáculo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 9, 'Cuando Moisés entraba en el tabernáculo, la columna de nube descendía y se ponía a la puerta del tabernáculo, y Jehová hablaba con Moisés. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 10, 'Y viendo todo el pueblo la columna de nube que estaba a la puerta del tabernáculo, se levantaba cada uno a la puerta de su tienda y adoraba.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 11, 'Y hablaba Jehová a Moisés cara a cara, como habla cualquiera a su compañero. Y él volvía al campamento; pero el joven Josué hijo de Nun, su servidor, nunca se apartaba de en medio del tabernáculo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 12, 'Y dijo Moisés a Jehová: Mira, tú me dices a mí: Saca este pueblo; y tú no me has declarado a quién enviarás conmigo. Sin embargo, tú dices: Yo te he conocido por tu nombre, y has hallado también gracia en mis ojos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 13, 'Ahora, pues, si he hallado gracia en tus ojos, te ruego que me muestres ahora tu camino, para que te conozca, y halle gracia en tus ojos; y mira que esta gente es pueblo tuyo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 14, 'Y él dijo: Mi presencia irá contigo, y te daré descanso.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 15, 'Y Moisés respondió: Si tu presencia no ha de ir conmigo, no nos saques de aquí. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 16, 'Y en qué se conocerá aquí que he hallado gracia en tus ojos, yo y tu pueblo, sino en que tú andes con nosotros, y que yo y tu pueblo seamos apartados de todos los pueblos que están sobre la faz de la tierra?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 17, 'Y Jehová dijo a Moisés: También haré esto que has dicho, por cuanto has hallado gracia en mis ojos, y te he conocido por tu nombre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 18, 'El entonces dijo: Te ruego que me muestres tu gloria.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 19, 'Y le respondió: Yo haré pasar todo mi bien delante de tu rostro, y proclamaré el nombre de Jehová delante de ti; y tendré misericordia del que tendré misericordia, y seré clemente para con el que seré clemente.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 20, 'Dijo más: No podrás ver mi rostro; porque no me verá hombre, y vivirá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 21, 'Y dijo aún Jehová: He aquí un lugar junto a mí, y tú estarás sobre la peña;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 22, 'y cuando pase mi gloria, yo te pondré en una hendidura de la peña, y te cubriré con mi mano hasta que haya pasado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 33, 23, 'Después apartaré mi mano, y verás mis espaldas; mas no se verá mi rostro.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 1, 'Cuando alguno hurtare buey u oveja, y lo degollare o vendiere, por aquel buey pagará cinco bueyes, y por aquella oveja cuatro ovejas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 2, 'Si el ladrón fuere hallado forzando una casa, y fuere herido y muriere, el que lo hirió no será culpado de su muerte.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 3, 'Pero si fuere de día, el autor de la muerte será reo de homicidio. El ladrón hará completa restitución; si no tuviere con qué, será vendido por su hurto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 4, 'Si fuere hallado con el hurto en la mano, vivo, sea buey o asno u oveja, pagará el doble.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 5, 'Si alguno hiciere pastar en campo o viña, y metiere su bestia en campo de otro, de lo mejor de su campo y de lo mejor de su viña pagará.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 6, 'Cuando se prendiere fuego, y al quemar espinos quemare mieses amontonadas o en pie, o campo, el que encendió el fuego pagará lo quemado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 7, 'Cuando alguno diere a su prójimo plata o alhajas a guardar, y fuere hurtado de la casa de aquel hombre, si el ladrón fuere hallado, pagará el doble.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 8, 'Si el ladrón no fuere hallado, entonces el dueño de la casa será presentado a los jueces, para que se vea si ha metido su mano en los bienes de su prójimo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 9, 'En toda clase de fraude, sobre buey, sobre asno, sobre oveja, sobre vestido, sobre toda cosa perdida, cuando alguno dijere: Esto es mío, la causa de ambos vendrá delante de los jueces; y el que los jueces condenaren, pagará el doble a su prójimo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 10, 'Si alguno hubiere dado a su prójimo asno, o buey, u oveja, o cualquier otro animal a guardar, y éste muriere o fuere estropeado, o fuere llevado sin verlo nadie;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 11, 'juramento de Jehová habrá entre ambos, de que no metió su mano a los bienes de su prójimo; y su dueño lo aceptará, y el otro no pagará.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 12, 'Mas si le hubiere sido hurtado, resarcirá a su dueño.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 13, 'Y si le hubiere sido arrebatado por fiera, le traerá testimonio, y no pagará lo arrebatado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 14, 'Pero si alguno hubiere tomado prestada bestia de su prójimo, y fuere estropeada o muerta, estando ausente su dueño, deberá pagarla.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 15, 'Si el dueño estaba presente no la pagará. Si era alquilada, reciba el dueño el alquiler.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 16, 'Si alguno engañare a una doncella que no fuere desposada, y durmiere con ella, deberá dotarla y tomarla por mujer.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 17, 'Si su padre no quisiere dársela, él le pesará plata conforme a la dote de las vírgenes.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 18, 'A la hechicera no dejarás que viva.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 19, 'Cualquiera que cohabitare con bestia, morirá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 20, 'El que ofreciere sacrificio a dioses excepto solamente a Jehová, será muerto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 21, 'Y al extranjero no engañarás ni angustiarás, porque extranjeros fuisteis vosotros en la tierra de Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 22, 'A ninguna viuda ni huérfano afligiréis.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 23, 'Porque si tú llegas a afligirles, y ellos clamaren a mí, ciertamente oiré yo su clamor;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 24, 'y mi furor se encenderá, y os mataré a espada, y vuestras mujeres serán viudas, y huérfanos vuestros hijos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 25, 'Cuando prestares dinero a uno de mi pueblo, al pobre que está contigo, no te portarás con él como logrero, ni le impondrás usura.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 26, 'Si tomares en prenda el vestido de tu prójimo, a la puesta del sol se lo devolverás.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 27, 'Porque sólo eso es su cubierta, es su vestido para cubrir su cuerpo. ¿En qué dormirá? Y cuando él clamare a mí, yo le oiré, porque soy misericordioso.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 28, 'No injuriarás a los jueces, ni maldecirás al príncipe de tu pueblo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 29, 'No demorarás la primicia de tu cosecha ni de tu lagar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 30, 'Lo mismo harás con el de tu buey y de tu oveja; siete días estará con su madre, y al octavo día me lo darás.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 22, 31, 'Y me seréis varones santos. No comeréis carne destrozada por las fieras en el campo; a los perros la echaréis.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 1, 'No admitirás falso rumor. No te concertarás con el impío para ser testigo falso.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 2, 'No seguirás a los muchos para hacer mal, ni responderás en litigio inclinándote a los más para hacer agravios;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 3, 'ni al pobre distinguirás en su causa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 4, 'Si encontrares el buey de tu enemigo o su asno extraviado, vuelve a llevárselo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 5, 'Si vieres el asno del que te aborrece caído debajo de su carga, ¿le dejarás sin ayuda? Antes bien le ayudarás a levantarlo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 6, 'No pervertirás el derecho de tu mendigo en su pleito.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 7, 'De palabra de mentira te alejarás, y no matarás al inocente y justo; porque yo no justificaré al impío.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 8, 'No recibirás presente; porque el presente ciega a los que ven, y pervierte las palabras de los justos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 9, 'Y no angustiarás al extranjero; porque vosotros sabéis cómo es el alma del extranjero, ya que extranjeros fuisteis en la tierra de Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 10, 'Seis años sembrarás tu tierra, y recogerás su cosecha;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 11, 'mas el séptimo año la dejarás libre, para que coman los pobres de tu pueblo; y de lo que quedare comerán las bestias del campo; así harás con tu viña y con tu olivar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 12, 'Seis días trabajarás, y al séptimo día reposarás, para que descanse tu buey y tu asno, y tome refrigerio el hijo de tu sierva, y el extranjero.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 13, 'Y todo lo que os he dicho, guardadlo. Y nombre de otros dioses no mentaréis, ni se oirá de vuestra boca.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 14, 'Tres veces en el año me celebraréis fiesta.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 15, 'La fiesta de los panes sin levadura guardarás. Siete días comerás los panes sin levadura, como yo te mandé, en el tiempo del mes de Abib, porque en él saliste de Egipto; y ninguno se presentará delante de mí con las manos vacías.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 16, 'También la fiesta de la siega, los primeros frutos de tus labores, que hubieres sembrado en el campo, y la fiesta de la cosecha a la salida del año, cuando hayas recogido los frutos de tus labores del campo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 17, 'Tres veces en el año se presentará todo varón delante de Jehová el Señor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 18, 'No ofrecerás con pan leudo la sangre de mi sacrificio, ni la grosura de mi víctima quedará de la noche hasta la mañana.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 19, 'Las primicias de los primeros frutos de tu tierra traerás a la casa de Jehová tu Dios. No guisarás el cabrito en la leche de su madre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 20, 'He aquí yo envío mi Angel delante de ti para que te guarde en el camino, y te introduzca en el lugar que yo he preparado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 21, 'Guárdate delante de él, y oye su voz; no le seas rebelde; porque él no perdonará vuestra rebelión, porque mi nombre está en él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 22, 'Pero si en verdad oyeres su voz e hicieres todo lo que yo te dijere, seré enemigo de tus enemigos, y afligiré a los que te afligieren.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 23, 'Porque mi Angel irá delante de ti, y te llevará a la tierra del amorreo, del heteo, del ferezeo, del cananeo, del heveo y del jebuseo, a los cuales yo haré destruir.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 24, 'No te inclinarás a sus dioses, ni los servirás, ni harás como ellos hacen; antes los destruirás del todo, y quebrarás totalmente sus estatuas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 25, 'Mas a Jehová vuestro Dios serviréis, y él bendecirá tu pan y tus aguas; y yo quitaré toda enfermedad de en medio de ti.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 26, 'No habrá mujer que aborte, ni estéril en tu tierra; y yo completaré el número de tus días.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 27, 'Yo enviaré mi terror delante de ti, y consternaré a todo pueblo donde entres, y te daré la cerviz de todos tus enemigos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 28, 'Enviaré delante de ti la avispa, que eche fuera al heveo, al cananeo y al heteo, de delante de ti.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 29, 'No los echaré de delante de ti en un año, para que no quede la tierra desierta, y se aumenten contra ti las fieras del campo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 30, 'Poco a poco los echaré de delante de ti, hasta que te multipliques y tomes posesión de la tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 31, 'Y fijaré tus límites desde el Mar Rojo hasta el mar de los filisteos, y desde el desierto hasta el Eufrates; porque pondré en tus manos a los moradores de la tierra, y tú los echarás de delante de ti.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 32, 'No harás alianza con ellos, ni con sus dioses.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 23, 33, 'En tu tierra no habitarán, no sea que te hagan pecar contra mí sirviendo a sus dioses, porque te será tropiezo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 1, 'Dijo Jehová a Moisés: Sube ante Jehová, tú, y Aarón, Nadab, y Abiú, y setenta de los ancianos de Israel; y os inclinaréis desde lejos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 2, 'Pero Moisés solo se acercará a Jehová; y ellos no se acerquen, ni suba el pueblo con él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 3, 'Y Moisés vino y contó al pueblo todas las palabras de Jehová, y todas las leyes; y todo el pueblo respondió a una voz, y dijo: Haremos todas las palabras que Jehová ha dicho.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 4, 'Y Moisés escribió todas las palabras de Jehová, y levantándose de mañana edificó un altar al pie del monte, y doce columnas, según las doce tribus de Israel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 5, 'Y envió jóvenes de los hijos de Israel, los cuales ofrecieron holocaustos y becerros como sacrificios de paz a Jehová.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 6, 'Y Moisés tomó la mitad de la sangre, y la puso en tazones, y esparció la otra mitad de la sangre sobre el altar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 7, 'Y tomó el libro del pacto y lo leyó a oídos del pueblo, el cual dijo: Haremos todas las cosas que Jehová ha dicho, y obedeceremos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 8, 'Entonces Moisés tomó la sangre y roció sobre el pueblo, y dijo: He aquí la sangre del pacto que Jehová ha hecho con vosotros sobre todas estas cosas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 9, 'Y subieron Moisés y Aarón, Nadab y Abiú, y setenta de los ancianos de Israel;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 10, 'y vieron al Dios de Israel; y había debajo de sus pies como un embaldosado de zafiro, semejante al cielo cuando está sereno.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 11, 'Mas no extendió su mano sobre los príncipes de los hijos de Israel; y vieron a Dios, y comieron y bebieron.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 12, 'Entonces Jehová dijo a Moisés: Sube a mí al monte, y espera allá, y te daré tablas de piedra, y la ley, y mandamientos que he escrito para enseñarles.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 13, 'Y se levantó Moisés con Josué su servidor, y Moisés subió al monte de Dios.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 14, 'Y dijo a los ancianos: Esperadnos aquí hasta que volvamos a vosotros; y he aquí Aarón y Hur están con vosotros; el que tuviere asuntos, acuda a ellos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 15, 'Entonces Moisés subió al monte, y una nube cubrió el monte.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 16, 'Y la gloria de Jehová reposó sobre el monte Sinaí, y la nube lo cubrió por seis días; y al séptimo día llamó a Moisés de en medio de la nube.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 17, 'Y la apariencia de la gloria de Jehová era como un fuego abrasador en la cumbre del monte, a los ojos de los hijos de Israel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 24, 18, 'Y entró Moisés en medio de la nube, y subió al monte; y estuvo Moisés en el monte cuarenta días y cuarenta noches.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 1, 'If a man shall steal an ox, or a sheep, and kill it, or sell it; he shall restore five oxen for an ox, and four sheep for a sheep.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 2, 'If a thief be found breaking up, and be smitten that he die, there shall no blood be shed for him.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 3, 'If the sun be risen upon him, there shall be blood shed for him; for he should make full restitution; if he have nothing, then he shall be sold for his theft.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 4, 'If the theft be certainly found in his hand alive, whether it be ox, or ass, or sheep; he shall restore double.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 5, 'If a man shall cause a field or vineyard to be eaten, and shall put in his beast, and shall feed in another mans field; of the best of his own field, and of the best of his own vineyard, shall he make restitution.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 6, 'If fire break out, and catch in thorns, so that the stacks of corn, or the standing corn, or the field, be consumed therewith; he that kindled the fire shall surely make restitution.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 7, 'If a man shall deliver unto his neighbour money or stuff to keep, and it be stolen out of the mans house; if the thief be found, let him pay double.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 8, 'If the thief be not found, then the master of the house shall be brought unto the judges, to see whether he have put his hand unto his neighbours goods.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 9, 'For all manner of trespass, whether it be for ox, for ass, for sheep, for raiment, or for any manner of lost thing which another challengeth to be his, the cause of both parties shall come before the judges; and whom the judges shall condemn, he shall pay double unto his neighbour.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 10, 'If a man deliver unto his neighbour an ass, or an ox, or a sheep, or any beast, to keep; and it die, or be hurt, or driven away, no man seeing it:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 11, 'Then shall an oath of the Lord be between them both, that he hath not put his hand unto his neighbours goods; and the owner of it shall accept thereof, and he shall not make it good.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 12, 'And if it be stolen from him, he shall make restitution unto the owner thereof.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 13, 'If it be torn in pieces, then let him bring it for witness, and he shall not make good that which was torn.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 14, 'And if a man borrow ought of his neighbour, and it be hurt, or die, the owner thereof being not with it, he shall surely make it good.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 15, 'But if the owner thereof be with it, he shall not make it good: if it be an hired thing, it came for his hire.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 16, 'And if a man entice a maid that is not betrothed, and lie with her, he shall surely endow her to be his wife.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 17, 'If her father utterly refuse to give her unto him, he shall pay money according to the dowry of virgins.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 18, 'Thou shalt not suffer a witch to live.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 19, 'Whosoever lieth with a beast shall surely be put to death.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 20, 'He that sacrificeth unto any god, save unto the Lord only, he shall be utterly destroyed.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 21, 'Thou shalt neither vex a stranger, nor oppress him: for ye were strangers in the land of Egypt.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 22, 'Ye shall not afflict any widow, or fatherless child.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 23, 'If thou afflict them in any wise, and they cry at all unto me, I will surely hear their cry;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 24, 'And my wrath shall wax hot, and I will kill you with the sword; and your wives shall be widows, and your children fatherless.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 25, 'If thou lend money to any of my people that is poor by thee, thou shalt not be to him as an usurer, neither shalt thou lay upon him usury.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 26, 'If thou at all take thy neighbours raiment to pledge, thou shalt deliver it unto him by that the sun goeth down:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 27, 'For that is his covering only, it is his raiment for his skin: wherein shall he sleep? and it shall come to pass, when he crieth unto me, that I will hear; for I am gracious.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 28, 'Thou shalt not revile the gods, nor curse the ruler of thy people.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 29, 'Thou shalt not delay to offer the first of thy ripe fruits, and of thy liquors: the firstborn of thy sons shalt thou give unto me.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 30, 'Likewise shalt thou do with thine oxen, and with thy sheep: seven days it shall be with his dam; on the eighth day thou shalt give it me.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 22, 31, 'And ye shall be holy men unto me: neither shall ye eat any flesh that is torn of beasts in the field; ye shall cast it to the dogs.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 1, 'Thou shalt not raise a false report: put not thine hand with the wicked to be an unrighteous witness.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 2, 'Thou shalt not follow a multitude to do evil; neither shalt thou speak in a cause to decline after many to wrest judgment:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 3, 'Neither shalt thou countenance a poor man in his cause.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 4, 'If thou meet thine enemys ox or his ass going astray, thou shalt surely bring it back to him again.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 5, 'If thou see the ass of him that hateth thee lying under his burden, and wouldest forbear to help him, thou shalt surely help with him.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 6, 'Thou shalt not wrest the judgment of thy poor in his cause.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 7, 'Keep thee far from a false matter; and the innocent and righteous slay thou not: for I will not justify the wicked.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 8, 'And thou shalt take no gift: for the gift blindeth the wise, and perverteth the words of the righteous.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 9, 'Also thou shalt not oppress a stranger: for ye know the heart of a stranger, seeing ye were strangers in the land of Egypt.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 10, 'And six years thou shalt sow thy land, and shalt gather in the fruits thereof:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 11, 'But the seventh year thou shalt let it rest and lie still; that the poor of thy people may eat: and what they leave the beasts of the field shall eat. In like manner thou shalt deal with thy vineyard, and with thy oliveyard.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 12, 'Six days thou shalt do thy work, and on the seventh day thou shalt rest: that thine ox and thine ass may rest, and the son of thy handmaid, and the stranger, may be refreshed.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 13, 'And in all things that I have said unto you be circumspect: and make no mention of the name of other gods, neither let it be heard out of thy mouth.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 14, 'Three times thou shalt keep a feast unto me in the year.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 15, 'Thou shalt keep the feast of unleavened bread: (thou shalt eat unleavened bread seven days, as I commanded thee, in the time appointed of the month Abib; for in it thou camest out from Egypt: and none shall appear before me empty:)');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 16, 'And the feast of harvest, the firstfruits of thy labours, which thou hast sown in the field: and the feast of ingathering, which is in the end of the year, when thou hast gathered in thy labours out of the field.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 17, 'Three times in the year all thy males shall appear before the Lord God.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 18, 'Thou shalt not offer the blood of my sacrifice with leavened bread; neither shall the fat of my sacrifice remain until the morning.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 19, 'The first of the firstfruits of thy land thou shalt bring into the house of the Lord thy God. Thou shalt not seethe a kid in his mothers milk.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 20, 'Behold, I send an Angel before thee, to keep thee in the way, and to bring thee into the place which I have prepared.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 21, 'Beware of him, and obey his voice, provoke him not; for he will not pardon your transgressions: for my name is in him.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 22, 'But if thou shalt indeed obey his voice, and do all that I speak; then I will be an enemy unto thine enemies, and an adversary unto thine adversaries.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 23, 'For mine Angel shall go before thee, and bring thee in unto the Amorites, and the Hittites, and the Perizzites, and the Canaanites, the Hivites, and the Jebusites: and I will cut them off.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 24, 'Thou shalt not bow down to their gods, nor serve them, nor do after their works: but thou shalt utterly overthrow them, and quite break down their images.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 25, 'And ye shall serve the Lord your God, and he shall bless thy bread, and thy water; and I will take sickness away from the midst of thee.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 26, 'There shall nothing cast their young, nor be barren, in thy land: the number of thy days I will fulfil.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 27, 'I will send my fear before thee, and will destroy all the people to whom thou shalt come, and I will make all thine enemies turn their backs unto thee.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 28, 'And I will send hornets before thee, which shall drive out the Hivite, the Canaanite, and the Hittite, from before thee.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 29, 'I will not drive them out from before thee in one year; lest the land become desolate, and the beast of the field multiply against thee.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 30, 'By little and little I will drive them out from before thee, until thou be increased, and inherit the land.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 31, 'And I will set thy bounds from the Red sea even unto the sea of the Philistines, and from the desert unto the river: for I will deliver the inhabitants of the land into your hand; and thou shalt drive them out before thee.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 32, 'Thou shalt make no covenant with them, nor with their gods.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 23, 33, 'They shall not dwell in thy land, lest they make thee sin against me: for if thou serve their gods, it will surely be a snare unto thee.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 1, 'And he said unto Moses, Come up unto the Lord, thou, and Aaron, Nadab, and Abihu, and seventy of the elders of Israel; and worship ye afar off.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 2, 'And Moses alone shall come near the Lord: but they shall not come nigh; neither shall the people go up with him.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 3, 'And Moses came and told the people all the words of the Lord, and all the judgments: and all the people answered with one voice, and said, All the words which the Lord hath said will we do.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 4, 'And Moses wrote all the words of the Lord, and rose up early in the morning, and builded an altar under the hill, and twelve pillars, according to the twelve tribes of Israel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 5, 'And he sent young men of the children of Israel, which offered burnt offerings, and sacrificed peace offerings of oxen unto the Lord.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 6, 'And Moses took half of the blood, and put it in basons; and half of the blood he sprinkled on the altar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 7, 'And he took the book of the covenant, and read in the audience of the people: and they said, All that the Lord hath said will we do, and be obedient.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 8, 'And Moses took the blood, and sprinkled it on the people, and said, Behold the blood of the covenant, which the Lord hath made with you concerning all these words.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 9, 'Then went up Moses, and Aaron, Nadab, and Abihu, and seventy of the elders of Israel:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 10, 'And they saw the God of Israel: and there was under his feet as it were a paved work of a sapphire stone, and as it were the body of heaven in his clearness.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 11, 'And upon the nobles of the children of Israel he laid not his hand: also they saw God, and did eat and drink.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 12, 'And the Lord said unto Moses, Come up to me into the mount, and be there: and I will give thee tables of stone, and a law, and commandments which I have written; that thou mayest teach them.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 13, 'And Moses rose up, and his minister Joshua: and Moses went up into the mount of God.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 14, 'And he said unto the elders, Tarry ye here for us, until we come again unto you: and, behold, Aaron and Hur are with you: if any man have any matters to do, let him come unto them.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 15, 'And Moses went up into the mount, and a cloud covered the mount.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 16, 'And the glory of the Lord abode upon mount Sinai, and the cloud covered it six days: and the seventh day he called unto Moses out of the midst of the cloud.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 17, 'And the sight of the glory of the Lord was like devouring fire on the top of the mount in the eyes of the children of Israel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 68, 24, 18, 'And Moses went into the midst of the cloud, and gat him up into the mount: and Moses was in the mount forty days and forty nights.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 1, 'Fue la vida de Sara ciento veintisiete años; tantos fueron los años de la vida de Sara.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 2, 'Y murió Sara en Quiriat-arba, que es Hebrón, en la tierra de Canaán; y vino Abraham a hacer duelo por Sara, y a llorarla.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 3, 'Y se levantó Abraham de delante de su muerta, y habló a los hijos de Het, diciendo:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 4, 'Extranjero y forastero soy entre vosotros; dadme propiedad para sepultura entre vosotros, y sepultaré mi muerta de delante de mí.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 5, 'Y respondieron los hijos de Het a Abraham, y le dijeron:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 6, 'Oyenos, señor nuestro; eres un príncipe de Dios entre nosotros; en lo mejor de nuestros sepulcros sepulta a tu muerta; ninguno de nosotros te negará su sepulcro, ni te impedirá que entierres tu muerta.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 7, 'Y Abraham se levantó, y se inclinó al pueblo de aquella tierra, a los hijos de Het,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 8, 'y habló con ellos, diciendo: Si tenéis voluntad de que yo sepulte mi muerta de delante de mí, oídme, e interceded por mí con Efrón hijo de Zohar,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 9, 'para que me dé la cueva de Macpela, que tiene al extremo de su heredad; que por su justo precio me la dé, para posesión de sepultura en medio de vosotros.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 10, 'Este Efrón estaba entre los hijos de Het; y respondió Efrón heteo a Abraham, en presencia de los hijos de Het, de todos los que entraban por la puerta de su ciudad, diciendo:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 11, 'No, señor mío, óyeme: te doy la heredad, y te doy también la cueva que está en ella; en presencia de los hijos de mi pueblo te la doy; sepulta tu muerta.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 12, 'Entonces Abraham se inclinó delante del pueblo de la tierra,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 13, 'y respondió a Efrón en presencia del pueblo de la tierra, deciendo: Antes, si te place, te ruego que me oigas. Yo daré el precio de la heredad; tómalo de mí, y sepultaré en ella mi muerta.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 14, 'Respondió Efrón a Abraham, diciéndole:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 15, 'Señor mío, escúchame: la tierra vale cuatrocientos siclos de plata; ¿qué es esto entre tú y yo? Entierra, pues, tu muerta.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 16, 'Entonces Abraham se convino con Efrón, y pesó Abraham a Efrón el dinero que dijo, en presencia de los hijos de Het, cuatrocientos siclos de plata, de buena ley entre mercaderes.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 17, 'Y quedó la heredad de Efrón que estaba en Macpela al oriente de Mamre, la heredad con la cueva que estaba en ella, y todos los árboles que había en la heredad, y en todos sus contornos,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 18, 'como propiedad de Abraham, en presencia de los hijos de Het y de todos los que entraban por la puerta de la ciudad.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 19, 'Después de esto sepultó Abraham a Sara su mujer en la cueva de la heredad de Macpela al oriente de Mamre, que es Hebrón, en la tierra de Canaán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 23, 20, 'Y quedó la heredad y la cueva que en ella había, de Abraham, como una posesión para sepultura, recibida de los hijos de Het.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 1, 'Era Abraham ya viejo, y bien avanzado en años; y Jehová había bendecido a Abraham en todo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 2, 'Y dijo Abraham a un criado suyo, el más viejo de su casa, que era el que gobernaba en todo lo que tenía: Pon ahora tu mano debajo de mi muslo,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 3, 'y te juramentaré por Jehová, Dios de los cielos y Dios de la tierra, que no tomarás para mi hijo mujer de las hijas de los cananeos, entre los cuales yo habito;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 4, 'sino que irás a mi tierra y a mi parentela, y tomarás mujer para mi hijo Isaac.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 5, 'El criado le respondió: Quizá la mujer no querrá venir en pos de mí a esta tierra. ¿Volveré, pues, tu hijo a la tierra de donde saliste?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 6, 'Y Abraham le dijo: Guárdate que no vuelvas a mi hijo allá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 7, 'Jehová, Dios de los cielos, que me tomó de la casa de mi padre y de la tierra de mi parentela, y me habló y me juró, diciendo: A tu descendencia daré esta tierra; él enviará su ángel delante de ti, y tú traerás de allá mujer para mi hijo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 8, 'Y si la mujer no quisiere venir en pos de ti, serás libre de este mi juramento; solamente que no vuelvas allá a mi hijo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 9, 'Entonces el criado puso su mano debajo del muslo de Abraham su señor, y le juró sobre este negocio.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 10, 'Y el criado tomó diez camellos de los camellos de su señor, y se fue, tomando toda clase de regalos escogidos de su señor; y puesto en camino, llegó a Mesopotamia, a la ciudad de Nacor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 11, 'E hizo arrodillar los camellos fuera de la ciudad, junto a un pozo de agua, a la hora de la tarde, la hora en que salen las doncellas por agua.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 12, 'Y dijo: Oh Jehová, Dios de mi señor Abraham, dame, te ruego, el tener hoy buen encuentro, y haz misericordia con mi señor Abraham.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 13, 'He aquí yo estoy junto a la fuente de agua, y las hijas de los varones de esta ciudad salen por agua.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 14, 'Sea, pues, que la doncella a quien yo dijere: Baja tu cántaro, te ruego, para que yo beba, y ella respondiere: Bebe, y también daré de beber a tus camellos; que sea ésta la que tú has destinado para tu siervo Isaac; y en esto conoceré que habrás hecho misericordia con mi señor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 15, 'Y aconteció que antes que él acabase de hablar, he aquí Rebeca, que había nacido a Betuel, hijo de Milca mujer de Nacor hermano de Abraham, la cual salía con su cántaro sobre su hombro.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 16, 'Y la doncella era de aspecto muy hermoso, virgen, a la que varón no había conocido; la cual descendió a la fuente, y llenó su cántaro, y se volvía.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 17, 'Entonces el criado corrió hacia ella, y dijo: Te ruego que me des a beber un poco de agua de tu cántaro.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 18, 'Ella respondió: Bebe, señor mío; y se dio prisa a bajar su cántaro sobre su mano, y le dio a beber.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 19, 'Y cuando acabó de darle de beber, dijo: También para tus camellos sacaré agua, hasta que acaben de beber.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 20, 'Y se dio prisa, y vació su cántaro en la pila, y corrió otra vez al pozo para sacar agua, y sacó para todos sus camellos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 21, 'Y el hombre estaba maravillado de ella, callando, para saber si Jehová había prosperado su viaje, o no.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 22, 'Y cuando los camellos acabaron de beber, le dio el hombre un pendiente de oro que pesaba medio siclo, y dos brazaletes que pesaban diez,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 23, 'y dijo: ¿De quién eres hija? Te ruego que me digas: ¿hay en casa de tu padre lugar donde posemos?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 24, 'Y ella respondió: Soy hija de Betuel hijo de Milca, el cual ella dio a luz a Nacor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 25, 'Y añadió: También hay en nuestra casa paja y mucho forraje, y lugar para posar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 26, 'El hombre entonces se inclinó, y adoró a Jehová,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 27, 'y dijo: Bendito sea Jehová, Dios de mi amo Abraham, que no apartó de mi amo su misericordia y su verdad, guiándome Jehová en el camino a casa de los hermanos de mi amo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 28, 'Y la doncella corrió, e hizo saber en casa de su madre estas cosas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 29, 'Y Rebeca tenía un hermano que se llamaba Labán, el cual corrió afuera hacia el hombre, a la fuente.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 30, 'Y cuando vio el pendiente y los brazaletes en las manos de su hermana, que decía: Así me habló aquel hombre, vino a él; y he aquí que estaba con los camellos junto a la fuente.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 31, 'Y le dijo: Ven, bendito de Jehová; ¿por qué estás fuera? He preparado la casa, y el lugar para los camellos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 32, 'Entonces el hombre vino a casa, y Labán desató los camellos; y les dio paja y forraje, y agua para lavar los pies de él, y los pies de los hombres que con él venían.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 33, 'Y le pusieron delante qué comer; mas él dijo: No comeré hasta que haya dicho mi mensaje. Y él le dijo: Habla.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 34, 'Entonces dijo: Yo soy criado de Abraham.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 35, 'Y Jehová ha bendecido mucho a mi amo, y él se ha engrandecido; y le ha dado ovejas y vacas, plata y oro, siervos y siervas, camellos y asnos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 36, 'Y Sara, mujer de mi amo, dio a luz en su vejez un hijo a mi señor, quien le ha dado a él todo cuanto tiene.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 37, 'Y mi amo me hizo jurar, diciendo: No tomarás para mi hijo mujer de las hijas de los cananeos, en cuya tierra habito;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 38, 'sino que irás a la casa de mi padre y a mi parentela, y tomarás mujer para mi hijo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 39, 'Y yo dije: Quizás la mujer no querrá seguirme.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 40, 'Entonces él me respondió: Jehová, en cuya presencia he andado, enviará su ángel contigo, y prosperará tu camino; y tomarás para mi hijo mujer de mi familia y de la casa de mi padre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 41, 'Entonces serás libre de mi juramento, cuando hayas llegado a mi familia; y si no te la dieren, serás libre de mi juramento.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 42, 'Llegué, pues, hoy a la fuente, y dije: Jehová, Dios de mi señor Abraham, si tú prosperas ahora mi camino por el cual ando,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 43, 'he aquí yo estoy junto a la fuente de agua; sea, pues, que la doncella que saliere por agua, a la cual dijere: Dame de beber, te ruego, un poco de agua de tu cántaro,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 44, 'y ella me respondiere: Bebe tú, y también para tus camellos sacaré agua; sea ésta la mujer que destinó Jehová para el hijo de mi señor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 45, 'Antes que acabase de hablar en mi corazón, he aquí Rebeca, que salía con su cántaro sobre su hombro; y descendió a la fuente, y sacó agua; y le dije: te ruego que me des de beber.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 46, 'Y bajó prontamente su cántaro de encima de sí, y dijo: Bebe, y también a tus camellos daré de beber. Y bebí, y dio también de beber a mis camellos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 47, 'Entonces le pregunté, y dije: ¿De quién eres hija? Y ella respondió: Hija de Betuel hijo de Nacor, que le dio a luz Milca. Entonces le puse un pendiente en su nariz, y brazaletes en sus brazos;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 48, 'y me incliné y adoré a Jehová, y bendije a Jehová Dios de mi señor Abraham, que me había guiado por camino de verdad para tomar la hija del hermano de mi señor para su hijo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 49, 'Ahora, pues, si vosotros hacéis misericordia y verdad con mi señor, declarádmelo; y si no, declarádmelo; y me iré a la diestra o a la siniestra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 50, 'Entonces Labán y Betuel respondieron y dijeron: De Jehová ha salido esto; no podemos hablarte malo ni bueno.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 51, 'He ahí Rebeca delante de ti; tómala y vete, y sea mujer del hijo de tu señor, como lo ha dicho Jehová.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 52, 'Cuando el criado de Abraham oyó sus palabras, se inclinó en tierra ante Jehová.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 53, 'Y sacó el criado alhajas de plata y alhajas de oro, y vestidos, y dio a Rebeca; también dio cosas preciosas a su hermano y a su madre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 54, 'Y comieron y bebieron él y los varones que venían con él, y durmieron; y levantándose de mañana, dijo: Enviadme a mi señor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 55, 'Entonces respondieron su hermano y su madre: Espere la doncella con nosotros a lo menos diez días, y después irá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 56, 'Y él les dijo: No me detengáis, ya que Jehová ha prosperado mi camino; despachadme para que me vaya a mi señor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 57, 'Ellos respondieron entonces: Llamemos a la doncella y preguntémosle.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 58, 'Y llamaron a Rebeca, y le dijeron: ¿Irás tú con este varón? Y ella respondió: Sí, iré.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 59, 'Entonces dejaron ir a Rebeca su hermana, y a su nodriza, y al criado de Abraham y a sus hombres.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 60, 'Y bendijeron a Rebeca, y le dijeron: Hermana nuestra, sé madre de millares de millares, y posean tus descendientes la puerta de sus enemigos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 61, 'Entonces se levantó Rebeca y sus doncellas, y montaron en los camellos, y siguieron al hombre; y el criado tomó a Rebeca, y se fue');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 62, 'Y venía Isaac del pozo del Viviente-que-me-ve; porque él habitaba en el Neguev.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 63, 'Y había salido Isaac a meditar al campo, a la hora de la tarde; y alzando sus ojos miró, y he aquí los camellos que venían.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 64, 'Rebeca también alzó sus ojos, y vio a Isaac, y descendió del camello;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 65, 'porque había preguntado al criado: ¿Quién es este varón que viene por el campo hacia nosotros? Y el criado había respondido: Este es mi señor. Ella entonces tomó el velo, y se cubrió.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 66, 'Entonces el criado contó a Isaac todo lo que había hecho.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 24, 67, 'Y la trajo Isaac a la tienda de su madre Sara, y tomó a Rebeca por mujer, y la amó; y se consoló Isaac después de la muerte de su madre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 1, 'Abraham tomó otra mujer, cuyo nombre era Cetura,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 2, 'la cual le dio a luz a Zimram, Jocsán, Medán, Madián, Isbac y Súa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 3, 'Y Jocsán engendró a Seba y a Dedán; e hijos de Dedán fueron Asurim, Letusim y Leumim.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 4, 'E hijos de Madián: Efa, Efer, Hanoc, Abida y Elda. Todos estos fueron hijos de Cetura.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 5, 'Y Abraham dio todo cuanto tenía a Isaac.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 6, 'Pero a los hijos de sus concubinas dio Abraham dones, y los envió lejos de Isaac su hijo, mientras él vivía, hacia el oriente, a la tierra oriental.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 7, 'Y estos fueron los días que vivió Abraham: ciento setenta y cinco años.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 8, 'Y exhaló el espíritu, y murió Abraham en buena vejez, anciano y lleno de años, y fue unido a su pueblo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 9, 'Y lo sepultaron Isaac e Ismael sus hijos en la cueva de Macpela, en la heredad de Efrón hijo de Zohar heteo, que está enfrente de Mamre,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 10, 'heredad que compró Abraham de los hijos de Het; allí fue sepultado Abraham, y Sara su mujer.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 11, 'Y sucedió, después de muerto Abraham, que Dios bendijo a Isaac su hijo; y habitó Isaac junto al pozo del Viviente-que-me- ve.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 12, 'Estos son los descendientes de Ismael hijo de Abraham, a quien le dio a luz Agar egipcia, sierva de Sara;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 13, 'estos, pues, son los nombres de los hijos de Ismael, nombrados en el orden de su nacimiento: El primogénito de Ismael, Nebaiot; luego Cedar, Adbeel, Mibsam,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 14, 'Misma, Duma, Massa,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 15, 'Hadar, Tema, Jetur, Nafis y Cedema.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 16, 'Estos son los hijos de Ismael, y estos sus nombres, por sus villas y por sus campamentos; doce príncipes por sus familias.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 17, 'Y estos fueron los años de la vida de Ismael, ciento treinta y siete años; y exhaló el espíritu Ismael, y murió, y fue unido a su pueblo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 18, 'Y habitaron desde Havila hasta Shur, que está enfrente de Egipto viniendo a Asiria; y murió en presencia de todos sus hermanos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 19, 'Estos son los descendientes de Isaac hijo de Abraham: Abraham engendró a Isaac,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 20, 'y era Isaac de cuarenta años cuando tomó por mujer a Rebeca, hija de Betuel arameo de Padan-aram, hermana de Labán arameo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 21, 'Y oró Isaac a Jehová por su mujer, que era estéril; y lo aceptó Jehová, y concibió Rebeca su mujer.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 22, 'Y los hijos luchaban dentro de ella; y dijo: Si es así, ¿para qué vivo yo? Y fue a consultar a Jehová;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 23, 'y le respondió Jehová: Dos naciones hay en tu seno, Y dos pueblos serán divididos desde tus entrañas; El un pueblo será más fuerte que el otro pueblo, Y el mayor servirá al menor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 24, 'Cuando se cumplieron sus días para dar a luz, he aquí había gemelos en su vientre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 25, 'Y salió el primero rubio, y era todo velludo como una pelliza; y llamaron su nombre Esaú.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 26, 'Después salió su hermano, trabada su mano al calcañar de Esaú; y fue llamado su nombre Jacob. Y era Isaac de edad de sesenta años cuando ella los dio a luz.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 27, 'Y crecieron los niños, y Esaú fue diestro en la caza, hombre del campo; pero Jacob era varón quieto, que habitaba en tiendas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 28, 'Y amó Isaac a Esaú, porque comía de su caza; mas Rebeca amaba a Jacob.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 29, 'Y guisó Jacob un potaje; y volviendo Esaú del campo, cansado,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 30, 'dijo a Jacob: Te ruego que me des a comer de ese guiso rojo, pues estoy muy cansado. Por tanto fue llamado su nombre Edom.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 31, 'Y Jacob respondió: Véndeme en este día tu primogenitura.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 32, 'Entonces dijo Esaú: He aquí yo me voy a morir; ¿para qué, pues, me servirá la primogenitura?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 33, 'Y dijo Jacob: Júramelo en este día. Y él le juró, y vendió a Jacob su primogenitura.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 25, 34, 'Entonces Jacob dio a Esaú pan y del guisado de las lentejas; y él comió y bebió, y se levantó y se fue. Así menospreció Esaú la primogenitura. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 1, 'The life of Sarah was one hundred and twenty-seven years; So many were the years of Sara''s life.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 2, 'And Sarah died in Kiriath-arba, which is Hebron, in the land of Canaan; and Abraham came to mourn for Sarah, and to weep for her.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 3, 'And Abraham arose from his dead woman, and spoke to the children of Heth, saying,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 4, 'I am a stranger and a stranger among you; Give me property for burial among you, and I will bury my dead from before me.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 5, 'And the sons of Heth answered Abraham, and said unto him,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 6, 'Hear us, our lord; You are a prince of God among us; In the best of our tombs bury your dead; None of us will deny you her burial, nor prevent you from burying your dead.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 7, 'And Abraham arose, and bowed down to the people of that land, to the children of Heth,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 8, 'and spoke to them, saying, If it is your will that I bury my dead from before me, hear me, and intercede for me with Ephron son of Zohar,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 9, 'to give me the cave of Machpelah, which is at the end of his inheritance; Let him give it to me for its fair price, for the possession of a grave among you.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 10, 'This Ephron was among the sons of Heth; And Ephron the Hittite answered Abraham, in the presence of the sons of Heth, of all who entered at the gate of his city, saying,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 11, 'No, my lord, hear me: I give you the inheritance, and I also give you the cave that is in her; In the presence of the children of my people I give it to you; Bury your dead.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 12, 'Then Abraham bowed down before the people of the land,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 13, 'and answered Ephron in the presence of the people of the land, saying: But if it pleases you, please listen to me. I will give the price of the inheritance; Take it from me, and I will bury my dead in it.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 14, 'Ephron answered Abraham, saying,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 15, 'My Lord, listen to me: the land is worth four hundred shekels of silver; what is this between you and me? So bury your dead.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 16, 'Then Abraham agreed with Ephron, and Abraham weighed to Ephron the money that he had mentioned, in the presence of the sons of Heth, four hundred shekels of silver, of good sterling among merchants.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 17, 'And the field of Ephron that was in Machpelah on the east of Mamre, the field with the cave that was in it, and all the trees that were in the field, and in all its surroundings, remained,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 18, 'as Abraham''s property, in the presence of the sons of Heth and of all those who entered through the gate of the city.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 19, 'After this Abraham buried his wife Sarah in the cave of the field of Machpelah east of Mamre, which is Hebron, in the land of Canaan.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 23, 20, 'And the field and the cave that was in it remained of Abraham, as a possession for burial, received from the children of Heth');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 1, 'Abraham was already old, and well advanced in years; Jehovah had blessed Abraham in everything.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 2, 'And Abraham said to one of his servants, the eldest of his house, who ruled over all he had: Now put your hand under my thigh,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 3, 'and I will swear to you by Jehovah, the God of heaven and the God of the land, that you shall not take for my son a wife from the daughters of the Canaanites, among whom I dwell;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 4, 'but you will go to my country and to my kindred, and you will take a wife for my son Isaac.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 5, 'The servant answered him: Perhaps the woman will not want to come after me to this land. Shall your son then return to the land from which you came?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 6, 'And Abraham said to him, Take care that you do not return to my son there.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 7, 'Jehovah, God of heaven, who took me from my father''s house and from the land of my kindred, and spoke to me and swore to me, saying, To your descendants I will give this land; He will send his angel before you, and you will bring a wife for my son from there.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 8, 'And if the woman does not want to come after you, you will be free from this my oath; Just don''t return my son there.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 9, 'Then the servant put his hand under the thigh of his master Abraham, and swore to him about this matter.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 10, 'And the servant took ten camels of his master''s camels, and went away, taking all kinds of choice gifts from his master; and setting out, he came to Mesopotamia, to the city of Nahor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 11, 'And he made the camels kneel outside the city, by a well of water, at the time of the evening, the time when the maidens go out to draw water.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 12, 'And he said: O Jehovah, God of my lord Abraham, grant me, I pray thee, a good meeting this day, and show mercy to my lord Abraham.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 13, 'Behold, I am at the fountain of water, and the daughters of the men of this city go out to draw water.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 14, 'Let it be then that the maiden to whom I say, Let down your pitcher, I pray thee, that I may drink, and she may answer, Drink, and I will also water your camels; May this be the one that you have destined for your servant Isaac; and by this I will know that you have shown mercy to my lord.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 15, 'And it came to pass, before he had finished speaking, that, behold, Rebekah, who had been born to Bethuel, son of Milcah, wife of Nahor, Abraham''s brother, came out with her waterpot on her shoulder.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 16, 'And the maiden was very beautiful in appearance, a virgin, whom no man had known; she which she descended to the fountain, and she filled the pitcher with it, and she returned.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 17, 'Then the servant ran to her and said, Please give me a little water from your pitcher to drink.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 18, 'She answered, Drink, my lord; and she hastened to lower her pitcher onto her hand, and she gave him to drink.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 19, 'And when she had finished watering him, she said, I will also draw water for your camels, until they have finished drinking.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 20, 'And she made haste, and emptied her pitcher into the basin, and ran again to the well to draw water, and drew her camels for all.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 21, 'And the man was amazed at her, keeping silent about her, to know whether Jehovah had prospered her journey or not.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 22, 'And when the camels had finished drinking, she gave the man a gold earring weighing half a shekel, and two bracelets weighing ten,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 23, 'and said, Whose daughter are you? Please tell me: is there a place in your father''s house where we can lodge?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 24, '24 And she answered, I am the daughter of Bethuel the son of Milcah, whom she bore to Nahor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 25, 'And she added: There is also in our house straw and much fodder, and a place to rest.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 26, 'Then the man bowed down and worshiped Jehovah,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 27, 'and said, Blessed be Jehovah, the God of my master Abraham, who has not withheld his mercy and his truth from my master, since Jehovah has guided me on the way to the house of my brothers. love.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 28, 'And the damsel ran and told these things in her mother''s house.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 29, 'And Rebekah had a brother named Laban, who ran outside to the man, to the fountain.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 30, 'And when she saw the earring and the bracelets in the hands of her sister, saying, Thus spoke that man to me, she came to him; and behold, he was with the camels by the fountain.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 31, 'And she said to him, Come, blessed of Jehovah; why are you out? I have prepared the house, and the place for the camels.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 32, 'Then the man came home, and Laban untied the camels; and he gave them straw and fodder, and water to wash his feet, and the feet of the men who came with him.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 33, 'And they set before him what to eat; but he said, I will not eat until I have spoken my message. And he said to him: Speak.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 34, 'Then he said, I am Abraham''s servant.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 35, 'And Jehovah has blessed my master greatly, and he has become great; and he has given him sheep and herds, silver and gold, male and female servants, camels and donkeys.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 36, 'And Sarah, my master''s wife, bore a son to my lord in her old age, who gave him all that she had.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 37, 'And my master made me swear, saying, Thou shalt not take a wife for my son from the daughters of the Canaanites, in whose land I dwell;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 38, 'halt not take a wife for my son from the daughters of the Canaanites, in whose land I dwell; 38 But you will go to my father''s house and to my relatives, and you will take a wife for my son..');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 39, 'And I said: Perhaps the woman will not want to follow me.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 40, 'Then he answered me, Jehovah, before whom I have walked, will send his angel with you, and your way will prosper; and you will take a wife for my son from my family and from my father''s house.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 41, 'Then you will be free from my oath, when you have come to my family; and if they do not give it to you, you will be free from my oath.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 42, 'So I came to the fountain today and said, "Jehovah, God of my lord Abraham, if you now prosper my way in which I walk,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 43, 'behold, I am at the fountain of water; Let it be, therefore, that the damsel who goes out to get water, to whom I say: Give me to drink, I pray you, a little water from your jug,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 44, 'and she answers me: You drink, and I will also draw water for your camels; Let this be the woman whom Jehovah appointed for my lord''s son.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 45, 'Before I had finished speaking in my heart, behold, Rebekah came out with her pitcher on her shoulder; and she went down to the fountain, and drew water; and I said to him: please give me something to drink.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 46, 'And she quickly took down her pitcher from off her, and she said, Drink, and I will water your camels also. And I drank, and she also watered my camels.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 47, 'Then I asked her, and said, Whose daughter are you? And she answered, Daughter of Bethuel son of Nahor, whom Milca bore to him. Then she put an earring on her nose, and bracelets on her arms;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 48, 'And I bowed down and worshiped Jehovah, and blessed Jehovah God of my lord Abraham, who had guided me in the way of truth to take my lord''s brother''s daughter for his son.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 49, 'Now therefore, if you show mercy and truth to my lord, declare it to me; and if not, declare it to me; and I will go to the right or to the left.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 50, 'Then Laban and Bethuel answered and said, This has come from Jehovah; We cannot speak bad or good to you.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 51, 'Behold Rebekah before you; Take her and go, and let her be the wife of your master''s son, as Jehovah has said.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 52, 'When Abraham''s servant heard his words, he bowed down to the ground before Jehovah.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 53, 'And he brought out silver jewels, and gold jewels, and garments, and gave them to Rebekah; He also gave precious things to his brother and his mother.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 54, 'And he and the men who came with him ate and drank, and slept; And rising early in the morning, he said, Send me my lord.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 55, 'Then his brother and his mother answered, Let the maiden wait with us for at least ten days, and then she will go.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 56, 'And he said to them, Do not detain me, for Jehovah has made my way prosperous; Send me away to go to my lord.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 57, 'Then they answered, Let us call the damsel and ask her.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 58, 'And they called Rebekah, and said unto her, Will you go with this man? And she answered: Yes, I will go. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 59, 'Then they let go Rebekah her sister, and her nurse, and Abraham''s servant, and his men.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 60, 'And they blessed Rebekah, and said to her, Our sister, be the mother of thousands of thousands, and let your descendants possess the gate of her enemies.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 61, 'Then Rebekah and her maids arose, and mounted the camels, and followed the man; and the servant took Rebekah and departed.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 62, 'And Isaac came from the well of the Living-Who-Sees-Me; because he lived in the Negev.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 63, 'And Isaac had gone out to meditate in the field at evening; And he lifted up his eyes and looked, and behold, the camels were coming.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 64, 'Rebekah also lifted up her eyes, and she saw Isaac, and she got off the camel;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 65, 'for she had asked the servant: Who is this man who is coming across the field towards us? And the servant had answered: This is my lord. She then took the veil, and covered herself.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 66, 'Then the servant told Isaac everything he had done.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 24, 67, 'And Isaac brought her to the tent of his mother Sarah, and he took Rebekah to wife, and loved her; and Isaac was comforted after the death of his mother.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 1, 'Abraham took another wife, whose name was Keturah,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 2, 'and she bore him Zimram, Jokshan, Medan, Midian, Ishbak, and Shuah.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 3, 'And Jokshan begat Sheba and Dedan; and the sons of Dedan were Ashurim, Letusim and Leumim.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 4, 'And the sons of Midian: Ephah, Epher, Hanoch, Abida and Elda. All these were sons of Keturah.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 5, 'And Abraham gave everything he had to Isaac.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 6, 'But to the sons of his concubines Abraham gave gifts, and he sent them away from his son Isaac, while he was alive, toward the east, to the eastern land.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 7, 'And these were the days that Abraham lived: one hundred and seventy-five years.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 8, 'And Abraham gave up the spirit, and Abraham died in good old age, old and full of years, and was joined to his people.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 9, 'And Isaac and Ishmael his sons buried him in the cave of Machpelah, in the field of Ephron the son of Zohar the Hittite, which is opposite Mamre,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 10, 'a field that Abraham bought from the children of Heth; There Abraham was buried, and his wife Sarah.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 11, 'And it came to pass, after Abraham was dead, that God blessed his son Isaac; and Isaac dwelt by the well of the Living-Who-Sees-Me.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 12, 'These are the descendants of Ishmael son of Abraham, to whom Hagar the Egyptian, Sarah''s servant, gave birth;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 13, 'These then are the names of Ishmael''s sons, named in the order of his birth: Ishmael''s firstborn, Nebaioth; then Kedar, Adbeel, Mibsam,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 14, 'Misma, Duma, Massa,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 15, 'Hadar, Tema, Jetur, Naphis and Kedema.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 16, 'These are the sons of Ishmael, and these are their names, according to their towns and their camps; twelve princes by their families.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 17, 'And these were the years of Ishmael''s life, one hundred and thirty-seven years; and he breathed out the spirit Ishmael, and died, and was joined to his people.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 18, 'And they dwelt from Havilah to Shur, which is opposite Egypt, coming to Assyria; and he died in the presence of all his brothers.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 19, 'These are the descendants of Isaac the son of Abraham: Abraham begat Isaac,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 20, 'and Isaac was forty years old when he took to wife Rebekah, daughter of Bethuel the Aramean of Padan-aram, sister of Laban the Aramean.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 21, 'And Isaac prayed to the Lord for his wife, who was barren; and the Jehovah accepted him, and his wife Rebekah conceived.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 22, 'And her sons fought within her; and she said: If so, why do I live? And he went to inquire of the Jehovah;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 23, 'And the Jehovah answered him:There are two nations in your bosom,And two peoples will be divided from your bowels;One town will be stronger than the other town,And the older will serve the younger.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 24, 'When her days for giving birth were completed, behold, there were twins in her womb.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 25, 'And the first one came out, blond, and he was all hairy like a coat; and they called his name Esau.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 26, 'Then his brother came out, his hand caught in Esau''s heel; and his name was called Jacob. And Isaac was sixty years old when she bore them.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 27, 'And the children grew up, and Esau was skilled in hunting, a man of the field; But Jacob was a quiet man, living in tents.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 28, 'And Isaac loved Esau, because he ate of his game; but Rebekah loved Jacob.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 29, 'And Jacob cooked porridge; And Esau returning from the field, tired,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 30, 'said to Jacob, Please give me this red stew to eat, for I am very tired. Therefore his name was called Edom.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 31, 'And Jacob said, Sell me this day your birthright.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 32, 'Then Esau said, Behold, I am going to die; What purpose, then, will my birthright be of use to me?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 33, 'And Jacob said, Swear to me this day. And he swore to him, and sold his birthright to Jacob.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (4, 67, 25, 34, 'Then Jacob gave Esau bread and lentil stew; and he ate and drank, and arose and departed. Thus Esau despised his birthright.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 1, 'Jehová habló a Moisés, diciendo:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 2, 'Conságrame todo primogénito. Cualquiera que abre matriz entre los hijos de Israel, así de los hombres como de los animales, mío es.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 3, 'Y Moisés dijo al pueblo: Tened memoria de este día, en el cual habéis salido de Egipto, de la casa de servidumbre, pues Jehová os ha sacado de aquí con mano fuerte; por tanto, no comeréis leudado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 4, 'Vosotros salís hoy en el mes de Abib.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 5, 'Y cuando Jehová te hubiere metido en la tierra del cananeo, del heteo, del amorreo, del heveo y del jebuseo, la cual juró a tus padres que te daría, tierra que destila leche y miel, harás esta celebración en este mes.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 6, 'Siete días comerás pan sin leudar, y el séptimo día será fiesta para Jehová.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 7, 'Por los siete días se comerán los panes sin levadura, y no se verá contigo nada leudado, ni levadura, en todo tu territorio.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 8, 'Y lo contarás en aquel día a tu hijo, diciendo: Se hace esto con motivo de lo que Jehová hizo conmigo cuando me sacó de Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 9, 'Y te será como una señal sobre tu mano, y como un memorial delante de tus ojos, para que la ley de Jehová esté en tu boca; por cuanto con mano fuerte te sacó Jehová de Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 10, 'Por tanto, tú guardarás este rito en su tiempo de año en año. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 11, 'Y cuando Jehová te haya metido en la tierra del cananeo, como te ha jurado a ti y a tus padres, y cuando te la hubiere dado,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 12, 'dedicarás a Jehová todo aquel que abriere matriz, y asimismo todo primer nacido de tus animales; los machos serán de Jehová.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 13, 'Mas todo primogénito de asno redimirás con un cordero; y si no lo redimieres, quebrarás su cerviz. También redimirás al primogénito de tus hijos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 14, 'Y cuando mañana te pregunte tu hijo, diciendo: ¿Qué es esto?, le dirás: Jehová nos sacó con mano fuerte de Egipto, de casa de servidumbre;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 15, 'y endureciéndose Faraón para no dejarnos ir, Jehová hizo morir en la tierra de Egipto a todo primogénito, desde el primogénito humano hasta el primogénito de la bestia; y por esta causa yo sacrifico para Jehová todo primogénito macho, y redimo al primogénito de mis hijos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 16, 'Te será, pues, como una señal sobre tu mano, y por un memorial delante de tus ojos, por cuanto Jehová nos sacó de Egipto con mano fuerte.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 17, 'Y luego que Faraón dejó ir al pueblo, Dios no los llevó por el camino de la tierra de los filisteos, que estaba cerca; porque dijo Dios: Para que no se arrepienta el pueblo cuando vea la guerra, y se vuelva a Egipto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 18, 'Mas hizo Dios que el pueblo rodease por el camino del desierto del Mar Rojo. Y subieron los hijos de Israel de Egipto armados.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 19, 'Tomó también consigo Moisés los huesos de José, el cual había juramentado a los hijos de Israel, diciendo: Dios ciertamente os visitará, y haréis subir mis huesos de aquí con vosotros.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 20, 'Y partieron de Sucot y acamparon en Etam, a la entrada del desierto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 21, 'Y Jehová iba delante de ellos de día en una columna de nube para guiarlos por el camino, y de noche en una columna de fuego para alumbrarles, a fin de que anduviesen de día y de noche.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 13, 22, 'Nunca se apartó de delante del pueblo la columna de nube de día, ni de noche la columna de fuego. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 1, 'Habló Jehová a Moisés, diciendo:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 2, 'Di a los hijos de Israel que den la vuelta y acampen delante de Pi-hahirot, entre Migdol y el mar hacia Baal-zefón; delante de él acamparéis junto al mar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 3, 'Porque Faraón dirá de los hijos de Israel: Encerrados están en la tierra, el desierto los ha encerrado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 4, 'Y yo endureceré el corazón de Faraón para que los siga; y seré glorificado en Faraón y en todo su ejército, y sabrán los egipcios que yo soy Jehová. Y ellos lo hicieron así.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 5, 'Y fue dado aviso al rey de Egipto, que el pueblo huía; y el corazón de Faraón y de sus siervos se volvió contra el pueblo, y dijeron: ¿Cómo hemos hecho esto de haber dejado ir a Israel, para que no nos sirva?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 6, 'Y unció su carro, y tomó consigo su pueblo;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 7, 'y tomó seiscientos carros escogidos, y todos los carros de Egipto, y los capitanes sobre ellos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 8, 'Y endureció Jehová el corazón de Faraón rey de Egipto, y él siguió a los hijos de Israel; pero los hijos de Israel habían salido con mano poderosa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 9, 'Siguiéndolos, pues, los egipcios, con toda la caballería y carros de Faraón, su gente de a caballo, y todo su ejército, los alcanzaron acampados junto al mar, al lado de Pi-hahirot, delante de Baal-zefón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 10, 'Y cuando Faraón se hubo acercado, los hijos de Israel alzaron sus ojos, y he aquí que los egipcios venían tras ellos; por lo que los hijos de Israel temieron en gran manera, y clamaron a Jehová.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 11, 'Y dijeron a Moisés: ¿No había sepulcros en Egipto, que nos has sacado para que muramos en el desierto? ¿Por qué has hecho así con nosotros, que nos has sacado de Egipto?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 12, '¿No es esto lo que te hablamos en Egipto, diciendo: Déjanos servir a los egipcios? Porque mejor nos fuera servir a los egipcios, que morir nosotros en el desierto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 13, 'Y Moisés dijo al pueblo: No temáis; estad firmes, y ved la salvación que Jehová hará hoy con vosotros; porque los egipcios que hoy habéis visto, nunca más para siempre los veréis.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 14, 'Jehová peleará por vosotros, y vosotros estaréis tranquilos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 15, 'Entonces Jehová dijo a Moisés: ¿Por qué clamas a mí? Di a los hijos de Israel que marchen.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 16, 'Y tú alza tu vara, y extiende tu mano sobre el mar, y divídelo, y entren los hijos de Israel por en medio del mar, en seco.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 17, 'Y he aquí, yo endureceré el corazón de los egipcios para que los sigan; y yo me glorificaré en Faraón y en todo su ejército, en sus carros y en su caballería;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 18, 'y sabrán los egipcios que yo soy Jehová, cuando me glorifique en Faraón, en sus carros y en su gente de a caballo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 19, 'Y el ángel de Dios que iba delante del campamento de Israel, se apartó e iba en pos de ellos; y asimismo la columna de nube que iba delante de ellos se apartó y se puso a sus espaldas,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 20, 'e iba entre el campamento de los egipcios y el campamento de Israel; y era nube y tinieblas para aquéllos, y alumbraba a Israel de noche, y en toda aquella noche nunca se acercaron los unos a los otros.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 21, 'Y extendió Moisés su mano sobre el mar, e hizo Jehová que el mar se retirase por recio viento oriental toda aquella noche; y volvió el mar en seco, y las aguas quedaron divididas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 22, 'Entonces los hijos de Israel entraron por en medio del mar, en seco, teniendo las aguas como muro a su derecha y a su izquierda.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 23, 'Y siguiéndolos los egipcios, entraron tras ellos hasta la mitad del mar, toda la caballería de Faraón, sus carros y su gente de a caballo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 24, 'Aconteció a la vigilia de la mañana, que Jehová miró el campamento de los egipcios desde la columna de fuego y nube, y trastornó el campamento de los egipcios,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 25, 'y quitó las ruedas de sus carros, y los trastornó gravemente. Entonces los egipcios dijeron: Huyamos de delante de Israel, porque Jehová pelea por ellos contra los egipcios.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 26, 'Y Jehová dijo a Moisés: Extiende tu mano sobre el mar, para que las aguas vuelvan sobre los egipcios, sobre sus carros, y sobre su caballería.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 27, 'Entonces Moisés extendió su mano sobre el mar, y cuando amanecía, el mar se volvió en toda su fuerza, y los egipcios al huir se encontraban con el mar; y Jehová derribó a los egipcios en medio del mar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 28, 'Y volvieron las aguas, y cubrieron los carros y la caballería, y todo el ejército de Faraón que había entrado tras ellos en el mar; no quedó de ellos ni uno.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 29, 'Y los hijos de Israel fueron por en medio del mar, en seco, teniendo las aguas por muro a su derecha y a su izquierda.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 30, 'Así salvó Jehová aquel día a Israel de mano de los egipcios; e Israel vio a los egipcios muertos a la orilla del mar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 14, 31, 'Y vio Israel aquel grande hecho que Jehová ejecutó contra los egipcios; y el pueblo temió a Jehová, y creyeron a Jehová y a Moisés su siervo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 1, 'Entonces cantó Moisés y los hijos de Israel este cántico a Jehová, y dijeron: Cantaré yo a Jehová, porque se ha magnificado grandemente; Ha echado en el mar al caballo y al jinete.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 2, 'Jehová es mi fortaleza y mi cántico, Y ha sido mi salvación. Este es mi Dios, y lo alabaré; Dios de mi padre, y lo enalteceré.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 3, 'Jehová es varón de guerra; Jehová es su nombre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 4, 'Echó en el mar los carros de Faraón y su ejército; Y sus capitanes escogidos fueron hundidos en el Mar Rojo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 5, 'Los abismos los cubrieron; Descendieron a las profundidades como piedra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 6, 'Tu diestra, oh Jehová, ha sido magnificada en poder; Tu diestra, oh Jehová, ha quebrantado al enemigo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 7, 'Y con la grandeza de tu poder has derribado a los que se levantaron contra ti. Enviaste tu ira; los consumió como a hojarasca.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 8, 'Al soplo de tu aliento se amontonaron las aguas; Se juntaron las corrientes como en un montón; Los abismos se cuajaron en medio del mar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 9, 'El enemigo dijo: Perseguiré, apresaré, repartiré despojos; Mi alma se saciará de ellos; Sacaré mi espada, los destruirá mi mano.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 10, 'Soplaste con tu viento; los cubrió el mar; Se hundieron como plomo en las impetuosas aguas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 11, '¿Quién como tú, oh Jehová, entre los dioses? ¿Quién como tú, magnífico en santidad, Terrible en maravillosas hazañas, hacedor de prodigios?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 12, 'Extendiste tu diestra; La tierra los tragó.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 13, 'Condujiste en tu misericordia a este pueblo que redimiste; Lo llevaste con tu poder a tu santa morada.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 14, 'Lo oirán los pueblos, y temblarán; Se apoderará dolor de la tierra de los filisteos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 15, 'Entonces los caudillos de Edom se turbarán; A los valientes de Moab les sobrecogerá temblor; Se acobardarán todos los moradores de Canaán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 16, 'Caiga sobre ellos temblor y espanto; A la grandeza de tu brazo enmudezcan como una piedra; Hasta que haya pasado tu pueblo, oh Jehová, Hasta que haya pasado este pueblo que tú rescataste.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 17, 'Tú los introducirás y los plantarás en el monte de tu heredad, En el lugar de tu morada, que tú has preparado, oh Jehová, En el santuario que tus manos, 2 oh Jehová, han afirmado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 18, 'Jehová reinará eternamente y para siempre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 19, 'Porque Faraón entró cabalgando con sus carros y su gente de a caballo en el mar, y Jehová hizo volver las aguas del mar sobre ellos; mas los hijos de Israel pasaron en seco por en medio del mar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 20, 'Y María la profetisa, hermana de Aarón, tomó un pandero en su mano, y todas las mujeres salieron en pos de ella con panderos y danzas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 21, 'Y María les respondía: Cantad a Jehová, porque en extremo se ha engrandecido; Ha echado en el mar al caballo y al jinete.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 22, 'E hizo Moisés que partiese Israel del Mar Rojo, y salieron al desierto de Shur; y anduvieron tres días por el desierto sin hallar agua.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 23, 'Y llegaron a Mara, y no pudieron beber las aguas de Mara, porque eran amargas; por eso le pusieron el nombre de Mara.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 24, 'Entonces el pueblo murmuró contra Moisés, y dijo: ¿Qué hemos de beber?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 25, 'Y Moisés clamó a Jehová, y Jehová le mostró un árbol; y lo echó en las aguas, y las aguas se endulzaron. Allí les dio estatutos y ordenanzas, y allí los probó;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 26, 'y dijo: Si oyeres atentamente la voz de Jehová tu Dios, e hicieres lo recto delante de sus ojos, y dieres oído a sus mandamientos, y guardares todos sus estatutos, ninguna enfermedad de las que envié a los egipcios te enviaré a ti; porque yo soy Jehová tu sanador.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 15, 27, 'Y llegaron a Elim, donde había doce fuentes de aguas, y setenta palmeras; y acamparon allí junto a las aguas.');
